using System;
using System.Collections.Generic;

enum CharacterType
{
    WARRIOR,
    MAGE,
    ARCHER
}

enum SkillType
{
    BASIC,
    SPECIAL
}

class GameCharacter
{
    public string Name { get; }
    public CharacterType Type { get; }
    public int BaseAttack { get; }
    public int Level { get; }

    public GameCharacter(string name, CharacterType type, int baseAttack, int level)
    {
        Name = name;
        Type = type;
        BaseAttack = baseAttack;
        Level = level;
    }
}

class Skill
{
    public SkillType Type { get; }
    public string Name { get; }

    public Skill(SkillType type, string name)
    {
        Type = type;
        Name = name;
    }
}

class Enemy
{
    public string Name { get; }
    public int Armor { get; }
    public int Resistance { get; }

    public Enemy(string name, int armor, int resistance)
    {
        Name = name;
        Armor = armor;
        Resistance = resistance;
    }
}

class DamageCalculator
{
    private readonly Random rng = new Random();

    public int CalculateDamage(GameCharacter c, Skill s, Enemy e)
    {
        int raw;
        switch (c.Type)
        {
            case CharacterType.WARRIOR:
                raw = (int)(c.BaseAttack * (1.2 + c.Level * 0.05));
                if (s.Type == SkillType.SPECIAL) raw = (int)(raw * 1.5);
                raw -= e.Armor;
                return Math.Max(raw, 0);

            case CharacterType.MAGE:
                raw = c.BaseAttack + (int)(c.Level * 3.5);
                if (s.Type == SkillType.SPECIAL) raw += 25;
                raw -= e.Resistance;
                return Math.Max(raw, 0);

            case CharacterType.ARCHER:
                raw = c.BaseAttack;
                bool crit = rng.Next(100) < (20 + c.Level);
                if (crit) raw *= 2;
                if (s.Type == SkillType.SPECIAL) raw += 10;
                raw -= (int)(e.Armor * 0.5);
                return Math.Max(raw, 0);

            default:
                throw new ArgumentException("Unknown type");
        }
    }
}

class LootCalculator
{
    public int CalculateGold(GameCharacter c, Enemy e)
    {
        switch (c.Type)
        {
            case CharacterType.WARRIOR:
                return 10 + c.Level + e.Armor / 5;
            case CharacterType.MAGE:
                return 8 + c.Level * 2 + e.Resistance / 4;
            case CharacterType.ARCHER:
                return 9 + (int)Math.Round(c.Level * 1.5);
            default:
                throw new ArgumentException("Unknown type");
        }
    }
}

