                return item.Size switch
                {
                    Size.SMALL => 250,
                    Size.MEDIUM => 320,
                    _ => 390
                };
            default: return 0;
        }
    }
}

class PromotionEngine
{
    public double Discount(FoodItem item)
    {
        switch (item.Type)
        {
            case FoodType.BURGER: return item.Extras.Contains("extra-cheese") ? 0.50 : 0.0;
            case FoodType.FRIES: return item.Size == Size.LARGE ? 0.20 : 0.0;
            case FoodType.DRINK: return item.Extras.Contains("no-ice") ? 0.10 : 0.0;
            case FoodType.SALAD: return item.Extras.Contains("chicken") ? 0.30 : 0.0;
            case FoodType.PIZZA: return item.Extras.Contains("pepperoni") ? 0.40 : 0.0;
            case FoodType.DESSERT: return 0.0;
            default: return 0.0;
        }
    }
}

class KitchenStationRouter
{
    public string Station(FoodItem item)
    {
        return item.Type switch
        {
            FoodType.BURGER => "Grill",
            FoodType.FRIES => "Fryer",
            FoodType.PIZZA => "Oven",
            FoodType.DRINK => "Beverage",
            FoodType.SALAD => "Cold Prep",
            FoodType.DESSERT => "Dessert Bar",
            _ => "Unknown"
        };
    }
}

class InventoryManager
{
    private readonly Dictionary<string, int> stock = new Dictionary<string, int>();

    public InventoryManager()
    {
        stock["bun"] = 50;
        stock["patty"] = 50;
        stock["potato"] = 80;
        stock["dough"] = 30;
        stock["soda"] = 100;
        stock["greens"] = 40;
        stock["dessertBase"] = 30;
    }

    public bool Reserve(FoodItem item)
    {
        switch (item.Type)
        {
            case FoodType.BURGER: return Take("bun", 1) && Take("patty", 1);
            case FoodType.FRIES: return Take("potato", 2);
            case FoodType.PIZZA: return Take("dough", 1);
            case FoodType.DRINK: return Take("soda", 1);
            case FoodType.SALAD: return Take("greens", 1);
            case FoodType.DESSERT: return Take("dessertBase", 1);
            default: return false;
        }
    }

    private bool Take(string key, int qty)
    {
        int have = stock.ContainsKey(key) ? stock[key] : 0;
        if (have < qty) return false;
        stock[key] = have - qty;
        return true;
    }

    public IReadOnlyDictionary<string, int> GetStock() => stock;
}

class Chef
{
    public void Prepare(FoodItem item)
    {
        switch (item.Type)
        {
            case FoodType.BURGER: Console.WriteLine("Chef grills patty and assembles burger"); break;
            case FoodType.FRIES: Console.WriteLine("Chef drops fries in fryer"); break;
            case FoodType.PIZZA: Console.WriteLine("Chef stretches dough and bakes pizza"); break;
            case FoodType.DRINK: Console.WriteLine("Chef fills cup and lids drink"); break;
            case FoodType.SALAD: Console.WriteLine("Chef chops and tosses salad"); break;
            case FoodType.DESSERT: Console.WriteLine("Chef plates dessert"); break;
        }
    }
}

class Waiter
{
    public void Take(Order order, FoodItem item)
    {
        Console.WriteLine("Waiter records item: " + item.Name);
        order.Add(item);
    }

    public void Serve(FoodItem item)
    {
        switch (item.Type)
        {
            case FoodType.BURGER: Console.WriteLine("Waiter serves burger with napkins"); break;
            case FoodType.FRIES: Console.WriteLine("Waiter serves fries with ketchup"); break;
            case FoodType.PIZZA: Console.WriteLine("Waiter serves pizza with slicer"); break;
            case FoodType.DRINK: Console.WriteLine("Waiter serves drink with straw"); break;
            case FoodType.SALAD: Console.WriteLine("Waiter serves salad with dressing"); break;
            case FoodType.DESSERT: Console.WriteLine("Waiter serves dessert with spoon"); break;
        }
    }
}

class Cashier
{
    private readonly PriceCalculator prices = new PriceCalculator();
    private readonly TaxCalculator taxes = new TaxCalculator();
    private readonly PromotionEngine promos = new PromotionEngine();

    public double Total(Order order)
    {
        double sum = 0.0;
        foreach (var item in order.Items())
        {
            double basePrice = prices.Price(item);
            double discount = promos.Discount(item);
            double taxed = (basePrice - discount) * (1.0 + taxes.TaxFor(item));
            sum += taxed;
        }
        return Math.Round(sum, 2);
    }
}

class ReceiptPrinter
{
    private readonly PriceCalculator prices = new PriceCalculator();
    private readonly TaxCalculator taxes = new TaxCalculator();
    private readonly PromotionEngine promos = new PromotionEngine();
    private readonly NutritionCalculator nutrition = new NutritionCalculator();

    public void Print(Order order)
    {
        Console.WriteLine("Customer: " + order.Customer);
        foreach (var item in order.Items())
        {
            double basePrice = prices.Price(item);
            double discount = promos.Discount(item);
            double taxRate = taxes.TaxFor(item);
            int kcal = nutrition.Calories(item);
            Console.WriteLine($"{item.Name} ({item.Type} {item.Size}) base ${basePrice} disc ${discount} tax {(int)(taxRate * 100)}% kcal {kcal}");
        }
    }
}

