enum FoodType { BURGER, FRIES, PIZZA, DRINK, SALAD, DESSERT }
enum Size { SMALL, MEDIUM, LARGE }

class FoodItem
{
    private readonly string name;
    private readonly FoodType type;
    private readonly Size size;
    private readonly HashSet<string> extras;

    public FoodItem(string name, FoodType type, Size size, HashSet<string> extras)
    {
        this.name = name;
        this.type = type;
        this.size = size;
        this.extras = extras ?? new HashSet<string>();
    }

    public string GetName() => name;
    public FoodType GetFoodType() => type; // avoid clash with object.GetType()
    public Size GetSize() => size;
    public HashSet<string> GetExtras() => extras;
}

class Order
{
    private readonly string customer;
    private readonly List<FoodItem> items = new List<FoodItem>();
    public Order(string customer) { this.customer = customer; }
    public void Add(FoodItem item) { items.Add(item); }
    public List<FoodItem> Items() => items;
    public string Customer() => customer;
}

interface FoodOps
{
    FoodType Type();
    double Tax();
    double Discount(FoodItem item);
    string Station();
    void Prepare();
    void Serve();
    bool Reserve(InventoryManager inventory, FoodItem item);
}

static class FoodOpsRegistry
{
    private static readonly Dictionary<FoodType, FoodOps> OPS = new Dictionary<FoodType, FoodOps>();

    public static void Register(FoodOps ops) => OPS[ops.Type()] = ops;

    public static FoodOps Of(FoodType t)
    {
        if (!OPS.TryGetValue(t, out var o)) throw new ArgumentException("Unknown type");
        return o;
    }

    static FoodOpsRegistry()
    {
        Register(new BurgerOps());
        Register(new FriesOps());
        Register(new PizzaOps());
        Register(new DrinkOps());
        Register(new SaladOps());
        Register(new DessertOps());
    }
}

