using System;
using System.Collections.Generic;

enum DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER }
enum RoomType { LIVING, KITCHEN, BEDROOM, GARDEN }
enum Mode { ECO, COMFORT, AWAY, NIGHT }

class SmartDevice {
    private readonly string id;
    private readonly DeviceType type;
    private Mode mode = Mode.ECO;
    private bool online = true;

    public SmartDevice(string id, DeviceType type) {
        this.id = id; this.type = type;
    }
    public string GetId() { return id; }
    public DeviceType GetTypeDevice() { return type; }
    public Mode GetMode() { return mode; }
    public bool IsOnline() { return online; }
    public void SetMode(Mode m) { this.mode = m; }
    public void SetOnline(bool o) { this.online = o; }
}

class Room {
    private readonly string name;
    private readonly RoomType type;
    private readonly List<SmartDevice> devices = new List<SmartDevice>();
    public Room(string name, RoomType type) { this.name = name; this.type = type; }
    public void Add(SmartDevice d) { devices.Add(d); }
    public List<SmartDevice> GetDevices() { return devices; }
    public string GetName() { return name; }
    public RoomType GetRoomType() { return type; }
}

class ScheduleEntry {
    public readonly SmartDevice device;
    public readonly string cron;
    public readonly string action;
    public ScheduleEntry(SmartDevice device, string cron, string action) {
        this.device = device; this.cron = cron; this.action = action;
    }
}

class SmartHomeController {
    public void TurnOn(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: Console.WriteLine("Light " + d.GetId() + " ON"); break;
            case DeviceType.THERMOSTAT: Console.WriteLine("Thermostat " + d.GetId() + " heating"); break;
            case DeviceType.DOOR_LOCK: Console.WriteLine("Lock " + d.GetId() + " stays locked"); break;
            case DeviceType.CAMERA: Console.WriteLine("Camera " + d.GetId() + " recording"); break;
            case DeviceType.SPRINKLER: Console.WriteLine("Sprinkler " + d.GetId() + " watering"); break;
        }
    }
    public void TurnOff(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: Console.WriteLine("Light " + d.GetId() + " OFF"); break;
            case DeviceType.THERMOSTAT: Console.WriteLine("Thermostat " + d.GetId() + " idle"); break;
            case DeviceType.DOOR_LOCK: Console.WriteLine("Lock " + d.GetId() + " remains locked"); break;
            case DeviceType.CAMERA: Console.WriteLine("Camera " + d.GetId() + " standby"); break;
            case DeviceType.SPRINKLER: Console.WriteLine("Sprinkler " + d.GetId() + " stopped"); break;
        }
    }
    public void SetMode(SmartDevice d, Mode m) {
        d.SetMode(m);
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: Console.WriteLine("Light " + d.GetId() + " mode " + m); break;
            case DeviceType.THERMOSTAT: Console.WriteLine("Thermostat " + d.GetId() + " mode " + m); break;
            case DeviceType.DOOR_LOCK: Console.WriteLine("Lock " + d.GetId() + " mode " + m); break;
            case DeviceType.CAMERA: Console.WriteLine("Camera " + d.GetId() + " mode " + m); break;
            case DeviceType.SPRINKLER: Console.WriteLine("Sprinkler " + d.GetId() + " mode " + m); break;
        }
    }
    public double EnergyUsageKwh(SmartDevice d, int minutes) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: return 0.012 * minutes;
            case DeviceType.THERMOSTAT: return 0.08 * minutes;
            case DeviceType.DOOR_LOCK: return 0.0002 * minutes;
            case DeviceType.CAMERA: return 0.01 * minutes;
            case DeviceType.SPRINKLER: return 0.03 * minutes;
            default: return 0;
        }
    }
    public string DeviceIcon(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: return "💡";
            case DeviceType.THERMOSTAT: return "🌡️";
            case DeviceType.DOOR_LOCK: return "🔒";
            case DeviceType.CAMERA: return "📷";
            case DeviceType.SPRINKLER: return "💧";
            default: return "❓";
        }
    }
    public List<string> FirmwareUpdateSteps(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: return new List<string> { "Ping", "Push FW", "Reboot" };
            case DeviceType.THERMOSTAT: return new List<string> { "Ping", "Backup Profile", "Push FW", "Recalibrate" };
            case DeviceType.DOOR_LOCK: return new List<string> { "Ping", "Secure Channel", "Push FW", "Reauth" };
            case DeviceType.CAMERA: return new List<string> { "Ping", "Push FW", "Restart Stream" };
            case DeviceType.SPRINKLER: return new List<string> { "Ping", "Push FW", "Valve Test" };
            default: return new List<string>();
        }
    }
}

class NotificationService {
    public void NotifyOwner(SmartDevice d, string ev) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: Console.WriteLine("Notify: Light " + d.GetId() + " -> " + ev); break;
            case DeviceType.THERMOSTAT: Console.WriteLine("Notify: Thermostat " + d.GetId() + " -> " + ev); break;
            case DeviceType.DOOR_LOCK: Console.WriteLine("Notify: Lock " + d.GetId() + " -> " + ev); break;
            case DeviceType.CAMERA: Console.WriteLine("Notify: Camera " + d.GetId() + " -> " + ev); break;
            case DeviceType.SPRINKLER: Console.WriteLine("Notify: Sprinkler " + d.GetId() + " -> " + ev); break;
        }
    }
}

class DiagnosticsService {
    public string Health(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: return d.IsOnline() ? "OK" : "Bulb Offline";
            case DeviceType.THERMOSTAT: return d.IsOnline() ? "OK" : "Sensor Offline";
            case DeviceType.DOOR_LOCK: return d.IsOnline() ? "OK" : "Battery Low";
            case DeviceType.CAMERA: return d.IsOnline() ? "OK" : "Stream Error";
            case DeviceType.SPRINKLER: return d.IsOnline() ? "OK" : "Valve Error";
            default: return "Unknown";
        }
    }
    public double MaintenanceCostYearly(SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT: return 5.0;
            case DeviceType.THERMOSTAT: return 20.0;
            case DeviceType.DOOR_LOCK: return 12.0;
            case DeviceType.CAMERA: return 18.0;
            case DeviceType.SPRINKLER: return 15.0;
            default: return 0.0;
        }
    }
}

class SecurityPolicy {
    public bool ActionAllowed(SmartDevice d, string action, bool userHome) {
        switch (d.GetTypeDevice()) {
            case DeviceType.DOOR_LOCK: return action.Equals("unlock") ? userHome : true;
            case DeviceType.CAMERA: return action.Equals("record") ? true : true;
            case DeviceType.THERMOSTAT: return true;
            case DeviceType.LIGHT: return true;
            case DeviceType.SPRINKLER: return !action.Equals("on") || !userHome;
            default: return false;
        }
    }
}

class VoiceCommandProcessor {
    private readonly SmartHomeController ctl = new SmartHomeController();
    public void Handle(string phrase, SmartDevice d) {
        switch (d.GetTypeDevice()) {
            case DeviceType.LIGHT:
                if (phrase.Equals("lights on", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("lights off", StringComparison.OrdinalIgnoreCase)) ctl.TurnOff(d);
                else if (phrase.Equals("night mode", StringComparison.OrdinalIgnoreCase)) ctl.SetMode(d, Mode.NIGHT);
                break;
            case DeviceType.THERMOSTAT:
                if (phrase.Equals("heat on", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("eco mode", StringComparison.OrdinalIgnoreCase)) ctl.SetMode(d, Mode.ECO);
                break;
            case DeviceType.DOOR_LOCK:
                if (phrase.Equals("lock door", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
            case DeviceType.CAMERA:
                if (phrase.Equals("start recording", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
            case DeviceType.SPRINKLER:
                if (phrase.Equals("water garden", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
        }
    }
}

class SceneManager {
    private readonly SmartHomeController ctl = new SmartHomeController();
    public void ApplyScene(string name, List<SmartDevice> devices) {
        foreach (SmartDevice d in devices) {
            switch (name) {
                case "Evening":
                    switch (d.GetTypeDevice()) {
                        case DeviceType.LIGHT: ctl.TurnOn(d); ctl.SetMode(d, Mode.COMFORT); break;
                        case DeviceType.THERMOSTAT: ctl.SetMode(d, Mode.COMFORT); break;
                        case DeviceType.CAMERA: ctl.TurnOn(d); break;
                        case DeviceType.DOOR_LOCK: ctl.TurnOn(d); break;
                        case DeviceType.SPRINKLER: ctl.TurnOff(d); break;
                    }
                    break;
                case "Away":
                    switch (d.GetTypeDevice()) {
                        case DeviceType.LIGHT: ctl.TurnOff(d); break;
                        case DeviceType.THERMOSTAT: ctl.SetMode(d, Mode.AWAY); break;
                        case DeviceType.CAMERA: ctl.TurnOn(d); break;
                        case DeviceType.DOOR_LOCK: ctl.TurnOn(d); break;
                        case DeviceType.SPRINKLER: ctl.TurnOff(d); break;
                    }
                    break;
            }
        }
    }
}

class EnergyEstimator {
    private readonly SmartHomeController ctl = new SmartHomeController();
    public double EstimateRoom(Room room, int minutes) {
