enum Metric { AREA, PERIMETER, VOLUME, DIAGONAL, BOUNDING_BOX_WIDTH }

class BoundingBox {
    public final double width;
    public final double height;
    public BoundingBox(double width, double height) { this.width = width; this.height = height; }
    @Override public String toString() { return "BBox(" + width + "x" + height + ")"; }
}

class MetricCalculator {
    public double compute(Shape shape, Metric metric) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return switch (metric) {
                    case AREA -> r.getWidth() * r.getHeight();
                    case PERIMETER -> 2 * (r.getWidth() + r.getHeight());
                    case DIAGONAL -> Math.hypot(r.getWidth(), r.getHeight());
                    case BOUNDING_BOX_WIDTH -> r.getWidth();
                    case VOLUME -> 0;
                };
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return switch (metric) {
                    case AREA -> Math.PI * c.getRadius() * c.getRadius();
                    case PERIMETER -> 2 * Math.PI * c.getRadius();
                    case DIAGONAL -> 2 * c.getRadius(); // diameter
                    case BOUNDING_BOX_WIDTH -> 2 * c.getRadius();
                    case VOLUME -> 0;
                };
            }
            case TRIANGLE: {
                Triangle t = (Triangle) shape;
                double s = (t.getA() + t.getB() + t.getC()) / 2.0;
                return switch (metric) {
                    case AREA -> Math.sqrt(Math.max(0, s * (s - t.getA()) * (s - t.getB()) * (s - t.getC())));
                    case PERIMETER -> (t.getA() + t.getB() + t.getC());
                    case DIAGONAL -> Math.max(t.getA(), Math.max(t.getB(), t.getC()));
                    case BOUNDING_BOX_WIDTH -> Math.max(t.getA(), Math.max(t.getB(), t.getC())); // bogus, but consistent
                    case VOLUME -> 0;
                };
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return switch (metric) {
                    case AREA -> 6 * cube.getEdge() * cube.getEdge();
                    case PERIMETER -> 12 * cube.getEdge();
                    case VOLUME -> Math.pow(cube.getEdge(), 3);
                    case DIAGONAL -> Math.sqrt(3) * cube.getEdge();
                    case BOUNDING_BOX_WIDTH -> cube.getEdge();
                };
            }
            case SPHERE: {
                Sphere s = (Sphere) shape;
                return switch (metric) {
                    case AREA -> 4 * Math.PI * s.getRadius() * s.getRadius();
                    case PERIMETER -> 0; // not meaningful
                    case VOLUME -> (4.0 / 3.0) * Math.PI * Math.pow(s.getRadius(), 3);
                    case DIAGONAL -> 2 * s.getRadius(); // diameter
                    case BOUNDING_BOX_WIDTH -> 2 * s.getRadius();
                };
            }
            default: return 0;
        }
    }
}

class ShapeValidator {
    public boolean isValid(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return r.getWidth() > 0 && r.getHeight() > 0;
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return c.getRadius() > 0;
            }
            case TRIANGLE: {
                Triangle t = (Triangle) shape;
                return t.getA() > 0 && t.getB() > 0 && t.getC() > 0
                    && t.getA() + t.getB() > t.getC()
                    && t.getA() + t.getC() > t.getB()
                    && t.getB() + t.getC() > t.getA();
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return cube.getEdge() > 0;
            }
            case SPHERE: {
                Sphere s = (Sphere) shape;
                return s.getRadius() > 0;
            }
            default: return false;
        }
    }
}

class ShapeRenderer {
    public String renderPseudoSvg(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return "<rect w=\"" + r.getWidth() + "\" h=\"" + r.getHeight()
                        + "\" fill=\"" + shape.getColor() + "\" layer=\"" + shape.getLayer() + "\"/>";
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return "<circle r=\"" + c.getRadius()
                        + "\" fill=\"" + shape.getColor() + "\" layer=\"" + shape.getLayer() + "\"/>";
            }
            case TRIANGLE: {
                Triangle t = (Triangle) shape;
                return "<polygon a=\"" + t.getA() + "\" b=\"" + t.getB() + "\" c=\"" + t.getC()
                        + "\" fill=\"" + shape.getColor() + "\" layer=\"" + shape.getLayer() + "\"/>";
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return "<cube edge=\"" + cube.getEdge()
                        + "\" materialColor=\"" + shape.getColor() + "\" layer=\"" + shape.getLayer() + "\"/>";
            }
            case SPHERE: {
                Sphere s = (Sphere) shape;
                return "<sphere r=\"" + s.getRadius()
                        + "\" materialColor=\"" + shape.getColor() + "\" layer=\"" + shape.getLayer() + "\"/>";
            }
            default: return "<unknown/>";
        }
    }
}

class ShapeSerializer {
    public String toCsvRow(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return "rectangle," + r.getWidth() + "," + r.getHeight() + "," + shape.getColor() + "," + shape.getLayer();
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return "circle," + c.getRadius() + "," + shape.getColor() + "," + shape.getLayer();
            }
            case TRIANGLE: {
                Triangle t = (Triangle) shape;
                return "triangle," + t.getA() + "," + t.getB() + "," + t.getC() + "," + shape.getColor() + "," + shape.getLayer();
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return "cube," + cube.getEdge() + "," + shape.getColor() + "," + shape.getLayer();
            }
            case SPHERE: {
                Sphere s = (Sphere) shape;
                return "sphere," + s.getRadius() + "," + shape.getColor() + "," + shape.getLayer();
            }
            default: return "unknown";
        }
    }
}

class ShapeFactory {
    public Shape create(String spec) {
        String[] parts = spec.split(";");
        String type = parts[0].trim().toLowerCase();
        java.util.Map<String,String> map = new java.util.HashMap<>();
        for (int i = 1; i < parts.length; i++) {
            String[] kv = parts[i].split("=");
            if (kv.length == 2) map.put(kv[0].trim().toLowerCase(), kv[1].trim());
        }
        String color = map.getOrDefault("color", "black");
        int layer = Integer.parseInt(map.getOrDefault("layer", "0"));

        switch (type) {
            case "rectangle":
                return new Rectangle(
                    Double.parseDouble(map.getOrDefault("width","0")),
                    Double.parseDouble(map.getOrDefault("height","0")),
                    color, layer
                );
            case "circle":
                return new Circle(
                    Double.parseDouble(map.getOrDefault("radius","0")),
                    color, layer
                );
            case "triangle":
                return new Triangle(
                    Double.parseDouble(map.getOrDefault("a","0")),
                    Double.parseDouble(map.getOrDefault("b","0")),
                    Double.parseDouble(map.getOrDefault("c","0")),
                    color, layer
                );
            case "cube":
                return new Cube(
                    Double.parseDouble(map.getOrDefault("edge","0")),
                    color, layer
                );
            case "sphere":
                return new Sphere(
                    Double.parseDouble(map.getOrDefault("radius","0")),
                    color, layer
                );
            default:
                throw new IllegalArgumentException("Unknown shape type: " + type);
        }
    }
}

class ShapePricing {
    private final MetricCalculator calc = new MetricCalculator();

    public double price(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                double area = calc.compute(shape, Metric.AREA);
                return 0.5 * area + shape.getLayer() * 2;
            }
            case CIRCLE: {
                double area = calc.compute(shape, Metric.AREA);
                double diag = calc.compute(shape, Metric.DIAGONAL);
                return 0.6 * area + 0.1 * diag;
            }
            case TRIANGLE: {
                double per = calc.compute(shape, Metric.PERIMETER);
                return per * 0.8;
            }
            case CUBE: {
                double vol = calc.compute(shape, Metric.VOLUME);
                return 1.2 * vol;
            }
            case SPHERE: {
                double vol = calc.compute(shape, Metric.VOLUME);
                return 1.0 * vol + 10;
            }
            default: return 0;
        }
    }
}

class LegendBuilder {
    public String legendSymbol(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: return "▭";
            case CIRCLE:    return "◯";
            case TRIANGLE:  return "△";
            case CUBE:      return "⬛";
            case SPHERE:    return "◯";
            default:        return "?";
        }
    }
}

class CollisionEngine {
    public BoundingBox boundingBox(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return new BoundingBox(r.getWidth(), r.getHeight());
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                double d = 2 * c.getRadius();
                return new BoundingBox(d, d);
            }
            case TRIANGLE: {
                Triangle t = (Triangle) shape;
                // naive placeholder: longest side × (average of other sides)
                double max = Math.max(t.getA(), Math.max(t.getB(), t.getC()));
                double avg = (t.getA() + t.getB() + t.getC() - max) / 2.0;
                return new BoundingBox(max, avg);
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return new BoundingBox(cube.getEdge(), cube.getEdge());
            }
            case SPHERE: {
                Sphere s = (Sphere) shape;
                double d = 2 * s.getRadius();
                return new BoundingBox(d, d);
            }
            default: return new BoundingBox(0, 0);
        }
    }

    public boolean overlaps(Shape a, Shape b) {
        BoundingBox A = boundingBox(a);
        BoundingBox B = boundingBox(b);
        return (A.width * A.height) > 0 && (B.width * B.height) > 0;
    }
}

class ShapeReport {
    private final MetricCalculator calc = new MetricCalculator();
    private final ShapeSerializer ser = new ShapeSerializer();
    private final ShapeRenderer rnd = new ShapeRenderer();
    private final CollisionEngine col = new CollisionEngine();
    private final ShapePricing price = new ShapePricing();
    private final LegendBuilder legend = new LegendBuilder();

    public String summarize(java.util.List<Shape> shapes) {
        StringBuilder sb = new StringBuilder();
        sb.append("=== Shape Report (OCP-violating) ===\n");
        for (Shape s : shapes) {
            sb.append("ID ").append(s.getId())
              .append(" ").append(s.getKind())
              .append(" color=").append(s.getColor())
              .append(" layer=").append(s.getLayer()).append("\n");

            sb.append("  CSV: ").append(ser.toCsvRow(s)).append("\n");
            sb.append("  SVG: ").append(rnd.renderPseudoSvg(s)).append("\n");
            sb.append("  Legend: ").append(legend.legendSymbol(s)).append("\n");

            sb.append("  Metrics: ");
            sb.append(" area=").append(calc.compute(s, Metric.AREA));
            sb.append(" perim=").append(calc.compute(s, Metric.PERIMETER));
            sb.append(" diag=").append(calc.compute(s, Metric.DIAGONAL));
            if (s instanceof ThreeDShape) {
                sb.append(" vol=").append(calc.compute(s, Metric.VOLUME));
            }
            sb.append("\n");

            sb.append("  BBox: ").append(col.boundingBox(s)).append("\n");
            sb.append("  Price: ").append(price.price(s)).append("\n\n");
        }
        return sb.toString();
    }
}

