import java.util.*;
import java.util.function.Function;

abstract class Entity {
    private static long NEXT_ID = 1;
    private final long id;
    protected Entity() { this.id = NEXT_ID++; }
    public long getId() { return id; }
}

abstract class GraphicObject extends Entity {
    private final String color;
    private final int layer;
    protected GraphicObject(String color, int layer) { this.color = color; this.layer = layer; }
    public String getColor() { return color; }
    public int getLayer() { return layer; }
}

enum ShapeKind { RECTANGLE, CIRCLE, TRIANGLE, CUBE, SPHERE }

final class BoundingBox {
    public final double width;
    public final double height;
    public BoundingBox(double width, double height) { this.width = width; this.height = height; }
    @Override public String toString() { return "BBox(" + width + "x" + height + ")"; }
}

interface HasWidthHeight { double getWidth(); double getHeight(); }
interface HasRadius { double getRadius(); }
interface HasTriangleSides { double getA(); double getB(); double getC(); }
interface HasEdge { double getEdge(); }

abstract class Shape extends GraphicObject {
    private final ShapeKind kind;
    protected Shape(ShapeKind kind, String color, int layer) { super(color, layer); this.kind = kind; }
    public ShapeKind getKind() { return kind; }
    public abstract boolean isValid();
    public abstract String renderPseudoSvg();
    public abstract String toCsvRow();
    public abstract String legendSymbol();
    public abstract BoundingBox boundingBox();
    public abstract double price(MetricCalculator calc);
}

abstract class TwoDShape extends Shape {
    protected TwoDShape(ShapeKind kind, String color, int layer) { super(kind, color, layer); }
}
abstract class ThreeDShape extends Shape {
    protected ThreeDShape(ShapeKind kind, String color, int layer) { super(kind, color, layer); }
}

final class Rectangle extends TwoDShape implements HasWidthHeight {
    private final double width, height;
    public Rectangle(double width, double height, String color, int layer) { super(ShapeKind.RECTANGLE, color, layer); this.width = width; this.height = height; }
    public double getWidth() { return width; }
    public double getHeight() { return height; }
    public boolean isValid() { return width > 0 && height > 0; }
    public String renderPseudoSvg() { return "<rect w=\"" + width + "\" h=\"" + height + "\" fill=\"" + getColor() + "\" layer=\"" + getLayer() + "\"/>"; }
    public String toCsvRow() { return "rectangle," + width + "," + height + "," + getColor() + "," + getLayer(); }
    public String legendSymbol() { return "▭"; }
    public BoundingBox boundingBox() { return new BoundingBox(width, height); }
    public double price(MetricCalculator calc) {
        double area = calc.compute(this, "area");
        return 0.5 * area + getLayer() * 2;
    }
}

final class Circle extends TwoDShape implements HasRadius {
    private final double radius;
    public Circle(double radius, String color, int layer) { super(ShapeKind.CIRCLE, color, layer); this.radius = radius; }
    public double getRadius() { return radius; }
    public boolean isValid() { return radius > 0; }
    public String renderPseudoSvg() { return "<circle r=\"" + radius + "\" fill=\"" + getColor() + "\" layer=\"" + getLayer() + "\"/>"; }
    public String toCsvRow() { return "circle," + radius + "," + getColor() + "," + getLayer(); }
    public String legendSymbol() { return "◯"; }
    public BoundingBox boundingBox() { double d = 2 * radius; return new BoundingBox(d, d); }
    public double price(MetricCalculator calc) {
        double area = calc.compute(this, "area");
        double diag = calc.compute(this, "diagonal");
        return 0.6 * area + 0.1 * diag;
    }
}

final class Triangle extends TwoDShape implements HasTriangleSides {
    private final double a, b, c;
    public Triangle(double a, double b, double c, String color, int layer) { super(ShapeKind.TRIANGLE, color, layer); this.a = a; this.b = b; this.c = c; }
    public double getA() { return a; } public double getB() { return b; } public double getC() { return c; }
    public boolean isValid() { return a > 0 && b > 0 && c > 0 && a + b > c && a + c > b && b + c > a; }
    public String renderPseudoSvg() { return "<polygon a=\"" + a + "\" b=\"" + b + "\" c=\"" + c + "\" fill=\"" + getColor() + "\" layer=\"" + getLayer() + "\"/>"; }
    public String toCsvRow() { return "triangle," + a + "," + b + "," + c + "," + getColor() + "," + getLayer(); }
    public String legendSymbol() { return "△"; }
    public BoundingBox boundingBox() { double max = Math.max(a, Math.max(b, c)); double avg = (a + b + c - max) / 2.0; return new BoundingBox(max, avg); }
    public double price(MetricCalculator calc) {
        double per = calc.compute(this, "perimeter");
        return per * 0.8;
    }
}

final class Cube extends ThreeDShape implements HasEdge {
    private final double edge;
    public Cube(double edge, String color, int layer) { super(ShapeKind.CUBE, color, layer); this.edge = edge; }
    public double getEdge() { return edge; }
    public boolean isValid() { return edge > 0; }
    public String renderPseudoSvg() { return "<cube edge=\"" + edge + "\" materialColor=\"" + getColor() + "\" layer=\"" + getLayer() + "\"/>"; }
    public String toCsvRow() { return "cube," + edge + "," + getColor() + "," + getLayer(); }
    public String legendSymbol() { return "⬛"; }
    public BoundingBox boundingBox() { return new BoundingBox(edge, edge); }
    public double price(MetricCalculator calc) {
        double vol = calc.compute(this, "volume");
        return 1.2 * vol;
    }
}

final class Sphere extends ThreeDShape implements HasRadius {
    private final double radius;
    public Sphere(double radius, String color, int layer) { super(ShapeKind.SPHERE, color, layer); this.radius = radius; }
    public double getRadius() { return radius; }
    public boolean isValid() { return radius > 0; }
    public String renderPseudoSvg() { return "<sphere r=\"" + radius + "\" materialColor=\"" + getColor() + "\" layer=\"" + getLayer() + "\"/>"; }
    public String toCsvRow() { return "sphere," + radius + "," + getColor() + "," + getLayer(); }
    public String legendSymbol() { return "◯"; }
    public BoundingBox boundingBox() { double d = 2 * radius; return new BoundingBox(d, d); }
    public double price(MetricCalculator calc) {
        double vol = calc.compute(this, "volume");
        return 1.0 * vol + 10;
    }
}

final class RegisteredMetric {
    private final String name;
    private final Map<Class<?>, Function<Object, Double>> handlers = new LinkedHashMap<>();
    public RegisteredMetric(String name) { this.name = name; }
    public String name() { return name; }
    public <T> RegisteredMetric on(Class<T> type, Function<T, Double> fn) { handlers.put(type, o -> fn.apply(type.cast(o))); return this; }
    public double apply(Shape s) {
        for (Map.Entry<Class<?>, Function<Object, Double>> e : handlers.entrySet()) {
            if (e.getKey().isInstance(s)) return e.getValue().apply(s);
        }
        return 0;
    }
}

final class MetricCalculator {
    private final Map<String, RegisteredMetric> metrics = new HashMap<>();
    public void register(RegisteredMetric metric) { metrics.put(metric.name(), metric); }
    public double compute(Shape shape, String metricName) {
        RegisteredMetric m = metrics.get(metricName);
        if (m == null) throw new IllegalArgumentException("Unknown metric: " + metricName);
        return m.apply(shape);
    }
}

