    public void register(String type, Creator c) { creators.put(type.toLowerCase(), c); }
    public Shape create(String spec) {
        String[] parts = spec.split(";");
        String type = parts[0].trim().toLowerCase();
        Map<String,String> map = new HashMap<>();
        for (int i = 1; i < parts.length; i++) {
            String[] kv = parts[i].split("=");
            if (kv.length == 2) map.put(kv[0].trim().toLowerCase(), kv[1].trim());
        }
        String color = map.getOrDefault("color", "black");
        int layer = Integer.parseInt(map.getOrDefault("layer", "0"));
        Creator c = creators.get(type);
        if (c == null) throw new IllegalArgumentException("Unknown shape type: " + type);
        return c.create(map, color, layer);
    }
}

final class ShapePricing {
    private final MetricCalculator calc;
    public ShapePricing(MetricCalculator calc) { this.calc = calc; }
