import java.util.*;

enum CharacterType { WARRIOR, MAGE, ARCHER, ROGUE }
enum SkillType { BASIC, SPECIAL, SUPPORT }
enum WeaponType { SWORD, STAFF, BOW, DAGGER }
enum PetType { WOLF, FAIRY, HAWK, SHADOW_CAT }

class Stats {
    int hp, mp, atk, mag, def, res, spd, crit;
    Stats(int hp,int mp,int atk,int mag,int def,int res,int spd,int crit){
        this.hp=hp; this.mp=mp; this.atk=atk; this.mag=mag; this.def=def; this.res=res; this.spd=spd; this.crit=crit;
    }
    Stats copy(){ return new Stats(hp,mp,atk,mag,def,res,spd,crit); }
}

class Skill {
    final String name; final SkillType type; final int cost;
    Skill(String n, SkillType t, int c){ name=n; type=t; cost=c; }
}

class Weapon {
    final String name; final WeaponType type; final int power;
    Weapon(String n, WeaponType t, int p){ name=n; type=t; power=p; }
}

class Pet {
    final String name; final PetType type; final int bond;
    Pet(String n, PetType t, int b){ name=n; type=t; bond=b; }
}

class Enemy {
    final String name; final Stats stats;
    Enemy(String n, Stats s){ name=n; stats=s; }
}

class GameCharacter {
    final String name; final CharacterType type; final int level; final Stats stats;
    final List<Skill> skills; final Weapon weapon; final Pet pet;
    GameCharacter(String n, CharacterType t, int lvl, Stats s, List<Skill> k, Weapon w, Pet p){
        name=n; type=t; level=lvl; stats=s; skills=k; weapon=w; pet=p;
    }
}

class PetBoostSystem {
    int offenseBoost(GameCharacter c){
        switch(c.pet.type){
            case WOLF: return (int)(c.stats.atk * Math.min(0.25, 0.05 + c.pet.bond/100.0));
            case FAIRY: return (int)(c.stats.mag * Math.min(0.25, 0.05 + c.pet.bond/120.0));
            case HAWK: return c.stats.spd/3 + c.pet.bond/6;
            case SHADOW_CAT: return 5 + c.pet.bond/5;
        }
        return 0;
    }
    int defenseBoost(GameCharacter c){
        switch(c.pet.type){
            case WOLF: return c.stats.def/6;
            case FAIRY: return c.stats.res/4;
            case HAWK: return c.stats.spd/6;
            case SHADOW_CAT: return 3;
        }
        return 0;
    }
    boolean petAssist(GameCharacter c){
        switch(c.pet.type){
            case WOLF: return new Random().nextInt(100) < Math.min(35, 10 + c.pet.bond/3);
            case FAIRY: return new Random().nextInt(100) < Math.min(40, 15 + c.pet.bond/2);
            case HAWK: return new Random().nextInt(100) < Math.min(30, 8 + c.pet.bond/4);
            case SHADOW_CAT: return new Random().nextInt(100) < Math.min(45, 12 + c.pet.bond/2);
        }
        return false;
    }
    int petAssistDamage(GameCharacter c){
        switch(c.pet.type){
            case WOLF: return 6 + c.pet.bond/4;
            case FAIRY: return 4 + c.pet.bond/5;
            case HAWK: return 5 + c.pet.bond/6;
            case SHADOW_CAT: return 7 + c.pet.bond/5;
        }
        return 0;
    }
    int petAssistHeal(GameCharacter c){
        switch(c.pet.type){
            case FAIRY: return 8 + c.pet.bond/3;
            default: return 0;
        }
    }
}

class WeaponRuleSet {
    int applyWeapon(GameCharacter c, int base){
        switch(c.weapon.type){
            case SWORD: return base + c.weapon.power + c.stats.atk/4;
            case STAFF: return base + c.weapon.power/2 + c.stats.mag/2;
            case BOW: return base + c.weapon.power + c.stats.spd/3;
            case DAGGER: return base + c.weapon.power + c.stats.spd/2;
        }
        return base;
    }
    int critChance(GameCharacter c){
        switch(c.weapon.type){
            case SWORD: return 8 + c.stats.crit;
            case STAFF: return 5 + c.stats.crit/2;
            case BOW: return 15 + c.stats.crit;
            case DAGGER: return 18 + c.stats.crit;
        }
        return 0;
    }
}

class DamageCalculator {
    final Random rng = new Random();
    final WeaponRuleSet wr = new WeaponRuleSet();
    final PetBoostSystem pets = new PetBoostSystem();

    int calc(GameCharacter c, Skill s, Enemy e){
        int base;
        switch(c.type){
            case WARRIOR:
                base = (int)(c.stats.atk * (1.1 + c.level*0.03)) - e.stats.def/3;
                if(s.type==SkillType.SPECIAL) base += 12;
                break;
            case MAGE:
                base = c.stats.mag*2 + (s.type==SkillType.SPECIAL?18:0) - e.stats.res/2;
                break;
            case ARCHER:
                base = c.stats.atk + c.stats.spd/2 + (s.type==SkillType.SPECIAL?10:0) - e.stats.def/4;
                break;
            case ROGUE:
                base = c.stats.atk + c.stats.spd + (s.type==SkillType.SPECIAL?14:0) - e.stats.def/5;
                break;
            default:
                base = 0;
        }
        base = wr.applyWeapon(c, base);
        base += pets.offenseBoost(c);
        if (pets.petAssist(c)) base += pets.petAssistDamage(c);
        int critChance = wr.critChance(c) + (c.type==CharacterType.ARCHER?10:0);
        if (rng.nextInt(100) < critChance) base *= 2;
        return Math.max(0, base);
    }
}

class HealCalculator {
    final PetBoostSystem pets = new PetBoostSystem();
    int heal(GameCharacter c, Skill s){
        int amt;
        switch(c.type){
            case WARRIOR: amt = c.stats.atk/3 + 6; break;
            case MAGE: amt = c.stats.mag + 14; break;
            case ARCHER: amt = c.stats.atk/4 + c.stats.spd/5; break;
            case ROGUE: amt = c.stats.atk/2; break;
            default: amt = 0;
        }
        if (s.type==SkillType.SPECIAL) amt += 6;
        amt += pets.defenseBoost(c)/2 + pets.petAssistHeal(c);
        return Math.max(0, amt);
    }
}

class LootCalculator {
    int gold(GameCharacter c, Enemy e){
        switch(c.type){
            case WARRIOR: return 10 + c.level + e.stats.def/5;
            case MAGE: return 9 + c.level*2 + e.stats.res/4;
            case ARCHER: return 11 + c.level + e.stats.def/6;
            case ROGUE: return 14 + c.level;
        }
        return 0;
    }
}

class TargetingSystem {
    Enemy pick(List<Enemy> enemies){
        Enemy t = enemies.get(0);
        for(Enemy e: enemies) if(e.stats.hp < t.stats.hp) t=e;
        return t;
    }
}

class SkillSelector {
    Skill choose(GameCharacter c){
        for(Skill s: c.skills) if(s.type!=SkillType.BASIC) return s;
        return c.skills.get(0);
    }
}

class TurnEngine {
    final DamageCalculator dmg=new DamageCalculator();
    final HealCalculator healCalc=new HealCalculator();
    final LootCalculator loot=new LootCalculator();
    final TargetingSystem targeting=new TargetingSystem();
    final SkillSelector selector=new SkillSelector();

    void turn(List<GameCharacter> party, List<Enemy> enemies){
        for(GameCharacter c: party){
            Skill s = selector.choose(c);
            if(s.type==SkillType.SUPPORT){
                int h = healCalc.heal(c,s);
                c.stats.hp += h;
                continue;
            }
            Enemy target = targeting.pick(enemies);
            int d = dmg.calc(c,s,target);
            target.stats.hp = Math.max(0, target.stats.hp - d);
        }
    }
    int totalGold(List<GameCharacter> party, Enemy e){
        int g=0; for(GameCharacter c: party) g+=loot.gold(c,e); return g;
    }
}

