import java.util.*;

enum FoodType { BURGER, FRIES, PIZZA, DRINK, SALAD, DESSERT }
enum Size { SMALL, MEDIUM, LARGE }

class FoodItem {
    private final String name;
    private final FoodType type;
    private final Size size;
    private final Set<String> extras;

    public FoodItem(String name, FoodType type, Size size, Set<String> extras) {
        this.name = name; this.type = type; this.size = size; this.extras = extras == null ? new HashSet<>() : extras;
    }
    public String getName() { return name; }
    public FoodType getType() { return type; }
    public Size getSize() { return size; }
    public Set<String> getExtras() { return extras; }
}

class Order {
    private final String customer;
    private final List<FoodItem> items = new ArrayList<>();
    public Order(String customer) { this.customer = customer; }
    public void add(FoodItem item) { items.add(item); }
    public List<FoodItem> items() { return items; }
    public String customer() { return customer; }
}

class PriceCalculator {
    public double price(FoodItem item) {
        switch (item.getType()) {
            case BURGER:
                switch (item.getSize()) {
                    case SMALL: return 5.49;
