class Cashier {
    private final PriceCalculator prices = new PriceCalculator();
    private final TaxCalculator taxes = new TaxCalculator();
    private final PromotionEngine promos = new PromotionEngine();
    public double total(Order order) {
        double sum = 0.0;
        for (FoodItem item : order.items()) {
            double base = prices.price(item);
            double discount = promos.discount(item);
            double taxed = (base - discount) * (1.0 + taxes.taxFor(item));
            sum += taxed;
        }
        return Math.round(sum * 100.0) / 100.0;
    }
}

class ReceiptPrinter {
    private final PriceCalculator prices = new PriceCalculator();
    private final TaxCalculator taxes = new TaxCalculator();
    private final PromotionEngine promos = new PromotionEngine();
