    public void print(Order order) {
        System.out.println("Customer: " + order.customer());
        for (FoodItem item : order.items()) {
            double base = prices.price(item);
            double discount = promos.discount(item);
            double taxRate = taxes.taxFor(item);
            int kcal = nutrition.calories(item);
            System.out.println(item.getName() + " (" + item.getType() + " " + item.getSize() + ") base $" + base + " disc $" + discount + " tax " + (int)(taxRate*100) + "% kcal " + kcal);
        }
    }
}

