import java.util.*;

enum DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER, BLIND, SPEAKER }
enum RoomType { LIVING, KITCHEN, BEDROOM, BATHROOM, GARDEN, GARAGE }
enum Mode { ECO, COMFORT, AWAY, NIGHT, PARTY }
enum AlertType { INFO, WARN, CRITICAL }

class SmartDevice {
    private final String id;
    private final DeviceType type;
    private Mode mode = Mode.ECO;
    private boolean online = true;

    public SmartDevice(String id, DeviceType type) { this.id = id; this.type = type; }
    public String getId() { return id; }
    public DeviceType getType() { return type; }
    public Mode getMode() { return mode; }
    public boolean isOnline() { return online; }
    public void setMode(Mode m) { mode = m; }
    public void setOnline(boolean o) { online = o; }
}

class Room {
    private final String name;
    private final RoomType type;
    private final List<SmartDevice> devices = new ArrayList<>();
    public Room(String name, RoomType type) { this.name = name; this.type = type; }
    public void add(SmartDevice d) { devices.add(d); }
    public List<SmartDevice> getDevices() { return devices; }
    public String getName() { return name; }
    public RoomType getType() { return type; }
}

class SmartHomeController {
    public void turnOn(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " ON"); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " heating"); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " locked"); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " recording"); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " watering"); break;
            case BLIND: System.out.println("Blind " + d.getId() + " open"); break;
            case SPEAKER: System.out.println("Speaker " + d.getId() + " playing"); break;
        }
    }
    public void turnOff(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " OFF"); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " idle"); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " secured"); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " standby"); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " stopped"); break;
            case BLIND: System.out.println("Blind " + d.getId() + " closed"); break;
            case SPEAKER: System.out.println("Speaker " + d.getId() + " paused"); break;
        }
    }
    public void setMode(SmartDevice d, Mode m) {
        d.setMode(m);
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " -> " + m); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " -> " + m); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " -> " + m); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " -> " + m); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " -> " + m); break;
            case BLIND: System.out.println("Blind " + d.getId() + " -> " + m); break;
            case SPEAKER: System.out.println("Speaker " + d.getId() + " -> " + m); break;
        }
    }
    public double energyUsageKwh(SmartDevice d, int minutes) {
        switch (d.getType()) {
            case LIGHT: return 0.012 * minutes;
            case THERMOSTAT: return 0.08 * minutes;
            case DOOR_LOCK: return 0.0002 * minutes;
            case CAMERA: return 0.01 * minutes;
            case SPRINKLER: return 0.03 * minutes;
            case BLIND: return minutes == 0 ? 0 : 0.001 * minutes;
            case SPEAKER: return 0.015 * minutes;
            default: return 0;
        }
    }
}

class NotificationService {
    public void push(SmartDevice d, AlertType t, String msg) {
        switch (d.getType()) {
            case LIGHT: System.out.println("[" + t + "] Light " + d.getId() + ": " + msg); break;
            case THERMOSTAT: System.out.println("[" + t + "] Thermostat " + d.getId() + ": " + msg); break;
            case DOOR_LOCK: System.out.println("[" + t + "] Lock " + d.getId() + ": " + msg); break;
            case CAMERA: System.out.println("[" + t + "] Camera " + d.getId() + ": " + msg); break;
            case SPRINKLER: System.out.println("[" + t + "] Sprinkler " + d.getId() + ": " + msg); break;
            case BLIND: System.out.println("[" + t + "] Blind " + d.getId() + ": " + msg); break;
            case SPEAKER: System.out.println("[" + t + "] Speaker " + d.getId() + ": " + msg); break;
        }
    }
}

class DiagnosticsService {
    public String health(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return d.isOnline() ? "OK" : "Bulb Offline";
            case THERMOSTAT: return d.isOnline() ? "OK" : "Sensor Offline";
            case DOOR_LOCK: return d.isOnline() ? "OK" : "Battery Low";
            case CAMERA: return d.isOnline() ? "OK" : "Stream Error";
            case SPRINKLER: return d.isOnline() ? "OK" : "Valve Error";
            case BLIND: return d.isOnline() ? "OK" : "Motor Jam";
            case SPEAKER: return d.isOnline() ? "OK" : "Audio Fault";
            default: return "Unknown";
        }
    }
}

class SecurityPolicy {
    public boolean allowed(SmartDevice d, String action, boolean userHome) {
        switch (d.getType()) {
            case DOOR_LOCK: return action.equals("unlock") ? userHome : true;
            case CAMERA: return !action.equals("disable") || userHome;
            case SPRINKLER: return !(action.equals("on") && userHome && d.getMode()==Mode.PARTY);
            case SPEAKER: return !(action.equals("play") && !userHome);
            default: return true;
        }
    }
}

class VoiceCommandProcessor {
    private final SmartHomeController ctl = new SmartHomeController();
    public void handle(String phrase, SmartDevice d) {
        switch (d.getType()) {
            case LIGHT:
                if (phrase.equalsIgnoreCase("lights on")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("lights off")) ctl.turnOff(d);
                break;
            case THERMOSTAT:
                if (phrase.equalsIgnoreCase("heat on")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("eco")) ctl.setMode(d, Mode.ECO);
                break;
            case DOOR_LOCK:
                if (phrase.equalsIgnoreCase("lock door")) ctl.turnOn(d);
                break;
            case CAMERA:
                if (phrase.equalsIgnoreCase("record")) ctl.turnOn(d);
                break;
            case SPRINKLER:
                if (phrase.equalsIgnoreCase("water")) ctl.turnOn(d);
                break;
            case BLIND:
                if (phrase.equalsIgnoreCase("open blinds")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("close blinds")) ctl.turnOff(d);
                break;
            case SPEAKER:
                if (phrase.equalsIgnoreCase("play music")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("pause music")) ctl.turnOff(d);
                break;
        }
    }
}

class SceneManager {
    private final SmartHomeController ctl = new SmartHomeController();
    public void apply(String scene, List<SmartDevice> devices) {
        for (SmartDevice d : devices) {
            switch (scene) {
                case "Evening":
                    switch (d.getType()) {
                        case LIGHT: ctl.turnOn(d); ctl.setMode(d, Mode.COMFORT); break;
                        case THERMOSTAT: ctl.setMode(d, Mode.COMFORT); break;
                        case CAMERA: ctl.turnOn(d); break;
                        case DOOR_LOCK: ctl.turnOn(d); break;
                        case SPRINKLER: ctl.turnOff(d); break;
                        case BLIND: ctl.turnOff(d); break;
                        case SPEAKER: ctl.turnOn(d); break;
                    }
                    break;
                case "Away":
                    switch (d.getType()) {
                        case LIGHT: ctl.turnOff(d); break;
                        case THERMOSTAT: ctl.setMode(d, Mode.AWAY); break;
                        case CAMERA: ctl.turnOn(d); break;
                        case DOOR_LOCK: ctl.turnOn(d); break;
                        case SPRINKLER: ctl.turnOff(d); break;
                        case BLIND: ctl.turnOff(d); break;
                        case SPEAKER: ctl.turnOff(d); break;
                    }
                    break;
                case "Party":
                    switch (d.getType()) {
                        case LIGHT: ctl.turnOn(d); ctl.setMode(d, Mode.PARTY); break;
                        case THERMOSTAT: ctl.setMode(d, Mode.COMFORT); break;
                        case SPEAKER: ctl.turnOn(d); break;
                        case CAMERA: ctl.turnOff(d); break;
                        case BLIND: ctl.turnOn(d); break;
                        case SPRINKLER: ctl.turnOff(d); break;
                        case DOOR_LOCK: ctl.turnOn(d); break;
                    }
                    break;
            }
        }
    }
}

class FirmwareManager {
    public List<String> steps(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return Arrays.asList("Ping","PushFW","Reboot");
            case THERMOSTAT: return Arrays.asList("Ping","Backup","PushFW","Recalibrate");
            case DOOR_LOCK: return Arrays.asList("SecureChannel","PushFW","Reauth");
            case CAMERA: return Arrays.asList("Ping","PushFW","RestartStream");
            case SPRINKLER: return Arrays.asList("Ping","PushFW","ValveTest");
            case BLIND: return Arrays.asList("Ping","PushFW","AlignMotor");
            case SPEAKER: return Arrays.asList("Ping","PushFW","RestartAudio");
            default: return Collections.emptyList();
        }
    }
}

class ScheduleEngine {
    static class Entry {
        final SmartDevice device; final String cron; final String action;
        Entry(SmartDevice d, String c, String a){ device=d; cron=c; action=a; }
    }
    public void run(Entry e, SmartHomeController ctl) {
        switch (e.device.getType()) {
            case LIGHT: if (e.action.equals("on")) ctl.turnOn(e.device); else ctl.turnOff(e.device); break;
            case SPRINKLER: if (e.action.equals("on")) ctl.turnOn(e.device); else ctl.turnOff(e.device); break;
            case THERMOSTAT: if (e.action.equals("comfort")) ctl.setMode(e.device, Mode.COMFORT); break;
            case BLIND: if (e.action.equals("open")) ctl.turnOn(e.device); else ctl.turnOff(e.device); break;
            case SPEAKER: if (e.action.equals("play")) ctl.turnOn(e.device); else ctl.turnOff(e.device); break;
            default: ctl.turnOff(e.device);
        }
    }
}

class WeatherAdvisor {
