    public SmartDevice find(String id) { return byId.get(id); }
    public List<String> groups(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return Arrays.asList("lighting","grid");
            case THERMOSTAT: return Arrays.asList("climate","grid");
            case DOOR_LOCK: return Arrays.asList("security");
            case CAMERA: return Arrays.asList("security","video");
            case SPRINKLER: return Arrays.asList("garden","water");
            case BLIND: return Arrays.asList("lighting","shades");
