class AutomationRuleEngine {
    private final SmartHomeController ctl = new SmartHomeController();
    public void onEvent(String event, SmartDevice d) {
        switch (event) {
            case "motion":
                switch (d.getType()) {
                    case CAMERA: ctl.turnOn(d); break;
                    case LIGHT: ctl.turnOn(d); break;
                    default: break;
                }
                break;
            case "sunset":
                switch (d.getType()) {
                    case LIGHT: ctl.turnOn(d); break;
                    case BLIND: ctl.turnOff(d); break;
                    default: break;
                }
                break;
            case "door-open":
                switch (d.getType()) {
                    case DOOR_LOCK: ctl.turnOn(d); break;
                    case CAMERA: ctl.turnOn(d); break;
                    default: break;
                }
                break;
        }
    }
}

class ReportPrinter {
    private final SmartHomeController ctl = new SmartHomeController();
    private final DiagnosticsService diag = new DiagnosticsService();
    private final DeviceRenderer render = new DeviceRenderer();
    public void printRoom(Room room) {
        System.out.println("Room: " + room.getName() + " (" + room.getType() + ")");
        for (SmartDevice d : room.getDevices()) {
            double e = ctl.energyUsageKwh(d, 30);
            System.out.println(render.icon(d) + " " + render.label(d) + " mode=" + d.getMode() + " health=" + diag.health(d) + " 30m=" + String.format("%.3f", e) + "kWh");
        }
    }
}

