abstract class GraphicObject {
    private final String color;

    protected GraphicObject(String color) {
        this.color = color;
    }

    public String getColor() {
        return color;
    }
}

enum ShapeKind {
    RECTANGLE, CIRCLE, CUBE
}

abstract class Shape extends GraphicObject {
    private final ShapeKind kind;

    protected Shape(ShapeKind kind, String color) {
        super(color);
        this.kind = kind;
    }

    public ShapeKind getKind() {
        return kind;
    }
}

abstract class TwoDShape extends Shape {
    protected TwoDShape(ShapeKind kind, String color) {
        super(kind, color);
    }
}

abstract class ThreeDShape extends Shape {
    protected ThreeDShape(ShapeKind kind, String color) {
        super(kind, color);
    }
}


class Rectangle extends TwoDShape {
    private final double width;
    private final double height;

    public Rectangle(double width, double height, String color) {
        super(ShapeKind.RECTANGLE, color);
        this.width = width;
        this.height = height;
    }

    public double getWidth()  { return width;  }
    public double getHeight() { return height; }
}

class Circle extends TwoDShape {
    private final double radius;

    public Circle(double radius, String color) {
        super(ShapeKind.CIRCLE, color);
        this.radius = radius;
    }

    public double getRadius() { return radius; }
}

class Cube extends ThreeDShape {
    private final double edge;

    public Cube(double edge, String color) {
        super(ShapeKind.CUBE, color);
        this.edge = edge;
    }

    public double getEdge() { return edge; }
}

enum Metric { AREA, PERIMETER, VOLUME }

class MetricCalculator {
    public double compute(Shape shape, Metric metric) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return switch (metric) {
                    case AREA      -> r.getWidth() * r.getHeight();
                    case PERIMETER -> 2 * (r.getWidth() + r.getHeight());
                    case VOLUME    -> 0;
                };
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return switch (metric) {
                    case AREA      -> Math.PI * c.getRadius() * c.getRadius();
                    case PERIMETER -> 2 * Math.PI * c.getRadius(); 
                    case VOLUME    -> 0;
                };
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return switch (metric) {
                    case AREA      -> 6 * cube.getEdge() * cube.getEdge();
                    case PERIMETER -> 12 * cube.getEdge();
                    case VOLUME    -> Math.pow(cube.getEdge(), 3);
                };
            }
            default:
                return 0;
        }
    }
}

class ShapeExporter {
    public String toJson(Shape shape) {
        switch (shape.getKind()) {
            case RECTANGLE: {
                Rectangle r = (Rectangle) shape;
                return String.format(
                    "{\"type\":\"rectangle\",\"color\":\"%s\",\"width\":%.2f,\"height\":%.2f}",
                    r.getColor(), r.getWidth(), r.getHeight()
                );
            }
            case CIRCLE: {
                Circle c = (Circle) shape;
                return String.format(
                    "{\"type\":\"circle\",\"color\":\"%s\",\"radius\":%.2f}",
                    c.getColor(), c.getRadius()
                );
            }
            case CUBE: {
                Cube cube = (Cube) shape;
                return String.format(
                    "{\"type\":\"cube\",\"color\":\"%s\",\"edge\":%.2f}",
                    cube.getColor(), cube.getEdge()
                );
            }
            default:
                return "{}";
        }
    }
}

class ShapeFactory {
    public Shape create(String spec) {
        String[] parts = spec.split(":", 2);
        String type = parts[0].trim();
        String[] kvs = (parts.length > 1 ? parts[1] : "").split(";");
        java.util.Map<String, String> map = new java.util.HashMap<>();
        for (String kv : kvs) {
            String[] p = kv.split("=");
            if (p.length == 2) map.put(p[0].trim(), p[1].trim());
        }
        String color = map.getOrDefault("color", "black");

        switch (type.toLowerCase()) {
            case "rectangle":
                return new Rectangle(
                    Double.parseDouble(map.getOrDefault("width", "0")),
                    Double.parseDouble(map.getOrDefault("height", "0")),
                    color
                );
            case "circle":
                return new Circle(
                    Double.parseDouble(map.getOrDefault("radius", "0")),
                    color
                );
            case "cube":
                return new Cube(
                    Double.parseDouble(map.getOrDefault("edge", "0")),
                    color
                );
            default:
                throw new IllegalArgumentException("Unknown shape: " + type);
        }
    }
}

