import java.util.*;

enum CharacterType {
    WARRIOR,
    MAGE,
    ARCHER
}

enum SkillType {
    BASIC,
    SPECIAL
}

class GameCharacter {
    private final String name;
    private final CharacterType type;
    private final int baseAttack;
    private final int level;

    public GameCharacter(String name, CharacterType type, int baseAttack, int level) {
        this.name = name;
        this.type = type;
        this.baseAttack = baseAttack;
        this.level = level;
    }

    public String getName() { return name; }
    public CharacterType getType() { return type; }
    public int getBaseAttack() { return baseAttack; }
    public int getLevel() { return level; }
}

class Skill {
    private final SkillType type;
    private final String name;

    public Skill(SkillType type, String name) {
        this.type = type;
        this.name = name;
    }

    public SkillType getType() { return type; }
    public String getName() { return name; }
}

class Enemy {
    private final String name;
    private final int armor;
    private final int resistance;

    public Enemy(String name, int armor, int resistance) {
        this.name = name;
        this.armor = armor;
        this.resistance = resistance;
    }

    public String getName() { return name; }
    public int getArmor() { return armor; }
    public int getResistance() { return resistance; }
}

class DamageCalculator {
    private final Random rng = new Random();

    public int calculateDamage(GameCharacter c, Skill s, Enemy e) {
        int raw;
        switch (c.getType()) {
            case WARRIOR:
                raw = (int) (c.getBaseAttack() * (1.2 + c.getLevel() * 0.05));
                if (s.getType() == SkillType.SPECIAL) raw *= 1.5;
                raw -= e.getArmor();
                return Math.max(raw, 0);
            case MAGE:
                raw = c.getBaseAttack() + (int) (c.getLevel() * 3.5);
                if (s.getType() == SkillType.SPECIAL) raw += 25;
                raw -= e.getResistance();
                return Math.max(raw, 0);
            case ARCHER:
                raw = c.getBaseAttack();
                boolean crit = rng.nextInt(100) < (20 + c.getLevel());
                if (crit) raw *= 2;
                if (s.getType() == SkillType.SPECIAL) raw += 10;
                raw -= (int) (e.getArmor() * 0.5);
                return Math.max(raw, 0);
            default:
                throw new IllegalArgumentException("Unknown type");
        }
    }
}

class LootCalculator {
    public int calculateGold(GameCharacter c, Enemy e) {
        switch (c.getType()) {
            case WARRIOR:
                return 10 + c.getLevel() + e.getArmor() / 5;
            case MAGE:
                return 8 + c.getLevel() * 2 + e.getResistance() / 4;
            case ARCHER:
                return 9 + (int) Math.round(c.getLevel() * 1.5);
            default:
                throw new IllegalArgumentException("Unknown type");
        }
    }
}

