class InventoryManager {
    private final Map<String, Integer> stock = new HashMap<>();
    public InventoryManager() {
        stock.put("bun", 50); stock.put("patty", 50); stock.put("potato", 80);
        stock.put("dough", 30); stock.put("soda", 100); stock.put("greens", 40); stock.put("dessertBase", 30);
    }
    public boolean reserve(FoodItem item) {
        switch (item.getType()) {
            case BURGER:
                return take("bun",1) && take("patty",1);
            case FRIES:
                return take("potato",2);
            case PIZZA:
                return take("dough",1);
            case DRINK:
                return take("soda",1);
            case SALAD:
                return take("greens",1);
            case DESSERT:
                return take("dessertBase",1);
            default:
                return false;
        }
    }
    private boolean take(String key, int qty) {
        int have = stock.getOrDefault(key,0);
        if (have < qty) return false;
        stock.put(key, have - qty);
        return true;
    }
    public Map<String,Integer> getStock(){ return Collections.unmodifiableMap(stock); }
}

class Chef {
