import java.util.*;

enum DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER }
enum RoomType { LIVING, KITCHEN, BEDROOM, GARDEN }
enum Mode { ECO, COMFORT, AWAY, NIGHT }

class SmartDevice {
    private final String id;
    private final DeviceType type;
    private Mode mode = Mode.ECO;
    private boolean online = true;

    public SmartDevice(String id, DeviceType type) {
        this.id = id; this.type = type;
    }
    public String getId() { return id; }
    public DeviceType getType() { return type; }
    public Mode getMode() { return mode; }
    public boolean isOnline() { return online; }
    public void setMode(Mode m) { this.mode = m; }
    public void setOnline(boolean o) { this.online = o; }
}

class Room {
    private final String name;
    private final RoomType type;
    private final List<SmartDevice> devices = new ArrayList<>();
    public Room(String name, RoomType type) { this.name = name; this.type = type; }
    public void add(SmartDevice d) { devices.add(d); }
    public List<SmartDevice> getDevices() { return devices; }
    public String getName() { return name; }
    public RoomType getType() { return type; }
}

class ScheduleEntry {
    public final SmartDevice device;
    public final String cron;
    public final String action;
    public ScheduleEntry(SmartDevice device, String cron, String action) {
        this.device = device; this.cron = cron; this.action = action;
    }
}

class SmartHomeController {
    public void turnOn(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " ON"); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " heating"); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " stays locked"); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " recording"); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " watering"); break;
        }
    }
    public void turnOff(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " OFF"); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " idle"); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " remains locked"); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " standby"); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " stopped"); break;
        }
    }
    public void setMode(SmartDevice d, Mode m) {
        d.setMode(m);
        switch (d.getType()) {
            case LIGHT: System.out.println("Light " + d.getId() + " mode " + m); break;
            case THERMOSTAT: System.out.println("Thermostat " + d.getId() + " mode " + m); break;
            case DOOR_LOCK: System.out.println("Lock " + d.getId() + " mode " + m); break;
            case CAMERA: System.out.println("Camera " + d.getId() + " mode " + m); break;
            case SPRINKLER: System.out.println("Sprinkler " + d.getId() + " mode " + m); break;
        }
    }
    public double energyUsageKwh(SmartDevice d, int minutes) {
        switch (d.getType()) {
            case LIGHT: return 0.012 * minutes;
            case THERMOSTAT: return 0.08 * minutes;
            case DOOR_LOCK: return 0.0002 * minutes;
            case CAMERA: return 0.01 * minutes;
            case SPRINKLER: return 0.03 * minutes;
            default: return 0;
        }
    }
    public String deviceIcon(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return "💡";
            case THERMOSTAT: return "🌡️";
            case DOOR_LOCK: return "🔒";
            case CAMERA: return "📷";
            case SPRINKLER: return "💧";
            default: return "❓";
        }
    }
    public List<String> firmwareUpdateSteps(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return Arrays.asList("Ping", "Push FW", "Reboot");
            case THERMOSTAT: return Arrays.asList("Ping", "Backup Profile", "Push FW", "Recalibrate");
            case DOOR_LOCK: return Arrays.asList("Ping", "Secure Channel", "Push FW", "Reauth");
            case CAMERA: return Arrays.asList("Ping", "Push FW", "Restart Stream");
            case SPRINKLER: return Arrays.asList("Ping", "Push FW", "Valve Test");
            default: return Collections.emptyList();
        }
    }
}

class NotificationService {
    public void notifyOwner(SmartDevice d, String event) {
        switch (d.getType()) {
            case LIGHT: System.out.println("Notify: Light " + d.getId() + " -> " + event); break;
            case THERMOSTAT: System.out.println("Notify: Thermostat " + d.getId() + " -> " + event); break;
            case DOOR_LOCK: System.out.println("Notify: Lock " + d.getId() + " -> " + event); break;
            case CAMERA: System.out.println("Notify: Camera " + d.getId() + " -> " + event); break;
            case SPRINKLER: System.out.println("Notify: Sprinkler " + d.getId() + " -> " + event); break;
        }
    }
}

class DiagnosticsService {
    public String health(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return d.isOnline() ? "OK" : "Bulb Offline";
            case THERMOSTAT: return d.isOnline() ? "OK" : "Sensor Offline";
            case DOOR_LOCK: return d.isOnline() ? "OK" : "Battery Low";
            case CAMERA: return d.isOnline() ? "OK" : "Stream Error";
            case SPRINKLER: return d.isOnline() ? "OK" : "Valve Error";
            default: return "Unknown";
        }
    }
    public double maintenanceCostYearly(SmartDevice d) {
        switch (d.getType()) {
            case LIGHT: return 5.0;
            case THERMOSTAT: return 20.0;
            case DOOR_LOCK: return 12.0;
            case CAMERA: return 18.0;
            case SPRINKLER: return 15.0;
            default: return 0.0;
        }
    }
}

class SecurityPolicy {
    public boolean actionAllowed(SmartDevice d, String action, boolean userHome) {
        switch (d.getType()) {
            case DOOR_LOCK: return action.equals("unlock") ? userHome : true;
            case CAMERA: return action.equals("record") ? true : true;
            case THERMOSTAT: return true;
            case LIGHT: return true;
            case SPRINKLER: return !action.equals("on") || !userHome;
            default: return false;
        }
    }
}

class VoiceCommandProcessor {
    private final SmartHomeController ctl = new SmartHomeController();
    public void handle(String phrase, SmartDevice d) {
        switch (d.getType()) {
            case LIGHT:
                if (phrase.equalsIgnoreCase("lights on")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("lights off")) ctl.turnOff(d);
                else if (phrase.equalsIgnoreCase("night mode")) ctl.setMode(d, Mode.NIGHT);
                break;
            case THERMOSTAT:
                if (phrase.equalsIgnoreCase("heat on")) ctl.turnOn(d);
                else if (phrase.equalsIgnoreCase("eco mode")) ctl.setMode(d, Mode.ECO);
                break;
            case DOOR_LOCK:
                if (phrase.equalsIgnoreCase("lock door")) ctl.turnOn(d);
                break;
            case CAMERA:
                if (phrase.equalsIgnoreCase("start recording")) ctl.turnOn(d);
                break;
            case SPRINKLER:
                if (phrase.equalsIgnoreCase("water garden")) ctl.turnOn(d);
                break;
        }
    }
