class SceneManager {
    private final SmartHomeController ctl = new SmartHomeController();
    public void applyScene(String name, List<SmartDevice> devices) {
        for (SmartDevice d : devices) {
            switch (name) {
                case "Evening":
                    switch (d.getType()) {
                        case LIGHT: ctl.turnOn(d); ctl.setMode(d, Mode.COMFORT); break;
                        case THERMOSTAT: ctl.setMode(d, Mode.COMFORT); break;
                        case CAMERA: ctl.turnOn(d); break;
                        case DOOR_LOCK: ctl.turnOn(d); break;
                        case SPRINKLER: ctl.turnOff(d); break;
                    }
                    break;
                case "Away":
                    switch (d.getType()) {
                        case LIGHT: ctl.turnOff(d); break;
                        case THERMOSTAT: ctl.setMode(d, Mode.AWAY); break;
                        case CAMERA: ctl.turnOn(d); break;
                        case DOOR_LOCK: ctl.turnOn(d); break;
                        case SPRINKLER: ctl.turnOff(d); break;
                    }
                    break;
            }
        }
    }
}

