import kotlin.math.*

abstract class Entity {
    companion object {
        private var NEXT_ID = 1L
    }
    val id: Long = NEXT_ID++
}

abstract class GraphicObject(val color: String, val layer: Int) : Entity()

enum class ShapeKind { RECTANGLE, CIRCLE, TRIANGLE, CUBE, SPHERE }

abstract class Shape(val kind: ShapeKind, color: String, layer: Int) : GraphicObject(color, layer)
abstract class TwoDShape(kind: ShapeKind, color: String, layer: Int) : Shape(kind, color, layer)
abstract class ThreeDShape(kind: ShapeKind, color: String, layer: Int) : Shape(kind, color, layer)

class Rectangle(val width: Double, val height: Double, color: String, layer: Int)
    : TwoDShape(ShapeKind.RECTANGLE, color, layer)

class Circle(val radius: Double, color: String, layer: Int)
    : TwoDShape(ShapeKind.CIRCLE, color, layer)

class Triangle(val a: Double, val b: Double, val c: Double, color: String, layer: Int)
    : TwoDShape(ShapeKind.TRIANGLE, color, layer)

class Cube(val edge: Double, color: String, layer: Int)
    : ThreeDShape(ShapeKind.CUBE, color, layer)

class Sphere(val radius: Double, color: String, layer: Int)
    : ThreeDShape(ShapeKind.SPHERE, color, layer)

enum class Metric { AREA, PERIMETER, VOLUME, DIAGONAL, BOUNDING_BOX_WIDTH }

data class BoundingBox(val width: Double, val height: Double) {
    override fun toString(): String = "BBox(${width}x$height)"
}

class MetricCalculator {
    fun compute(shape: Shape, metric: Metric): Double {
        return when (shape.kind) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                when (metric) {
                    Metric.AREA -> r.width * r.height
                    Metric.PERIMETER -> 2 * (r.width + r.height)
                    Metric.DIAGONAL -> hypot(r.width, r.height)
                    Metric.BOUNDING_BOX_WIDTH -> r.width
                    Metric.VOLUME -> 0.0
                }
            }
            ShapeKind.CIRCLE -> {
                val c = shape as Circle
                when (metric) {
                    Metric.AREA -> PI * c.radius * c.radius
                    Metric.PERIMETER -> 2 * PI * c.radius
                    Metric.DIAGONAL -> 2 * c.radius
                    Metric.BOUNDING_BOX_WIDTH -> 2 * c.radius
                    Metric.VOLUME -> 0.0
                }
            }
            ShapeKind.TRIANGLE -> {
                val t = shape as Triangle
                val s = (t.a + t.b + t.c) / 2.0
                when (metric) {
                    Metric.AREA -> sqrt(max(0.0, s * (s - t.a) * (s - t.b) * (s - t.c)))
                    Metric.PERIMETER -> t.a + t.b + t.c
                    Metric.DIAGONAL -> maxOf(t.a, t.b, t.c)
                    Metric.BOUNDING_BOX_WIDTH -> maxOf(t.a, t.b, t.c)
                    Metric.VOLUME -> 0.0
                }
            }
            ShapeKind.CUBE -> {
                val cube = shape as Cube
                when (metric) {
                    Metric.AREA -> 6 * cube.edge * cube.edge
                    Metric.PERIMETER -> 12 * cube.edge
                    Metric.VOLUME -> cube.edge.pow(3)
                    Metric.DIAGONAL -> sqrt(3.0) * cube.edge
                    Metric.BOUNDING_BOX_WIDTH -> cube.edge
                }
            }
            ShapeKind.SPHERE -> {
                val s = shape as Sphere
                when (metric) {
                    Metric.AREA -> 4 * PI * s.radius * s.radius
                    Metric.PERIMETER -> 0.0
                    Metric.VOLUME -> (4.0 / 3.0) * PI * s.radius.pow(3)
                    Metric.DIAGONAL -> 2 * s.radius
                    Metric.BOUNDING_BOX_WIDTH -> 2 * s.radius
                }
            }
        }
    }
}

class ShapeValidator {
    fun isValid(shape: Shape): Boolean {
        return when (shape.kind) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                r.width > 0 && r.height > 0
            }
            ShapeKind.CIRCLE -> (shape as Circle).radius > 0
            ShapeKind.TRIANGLE -> {
                val t = shape as Triangle
                t.a > 0 && t.b > 0 && t.c > 0 &&
                t.a + t.b > t.c && t.a + t.c > t.b && t.b + t.c > t.a
            }
            ShapeKind.CUBE -> (shape as Cube).edge > 0
            ShapeKind.SPHERE -> (shape as Sphere).radius > 0
        }
    }
}

class ShapeRenderer {
    fun renderPseudoSvg(shape: Shape): String {
        return when (shape.kind) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                "<rect w=\"${r.width}\" h=\"${r.height}\" fill=\"${shape.color}\" layer=\"${shape.layer}\"/>"
            }
            ShapeKind.CIRCLE -> {
                val c = shape as Circle
                "<circle r=\"${c.radius}\" fill=\"${shape.color}\" layer=\"${shape.layer}\"/>"
            }
            ShapeKind.TRIANGLE -> {
                val t = shape as Triangle
                "<polygon a=\"${t.a}\" b=\"${t.b}\" c=\"${t.c}\" fill=\"${shape.color}\" layer=\"${shape.layer}\"/>"
            }
            ShapeKind.CUBE -> {
                val cube = shape as Cube
                "<cube edge=\"${cube.edge}\" materialColor=\"${shape.color}\" layer=\"${shape.layer}\"/>"
            }
            ShapeKind.SPHERE -> {
                val s = shape as Sphere
                "<sphere r=\"${s.radius}\" materialColor=\"${shape.color}\" layer=\"${shape.layer}\"/>"
            }
        }
    }
}

class ShapeSerializer {
    fun toCsvRow(shape: Shape): String {
        return when (shape.kind) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                "rectangle,${r.width},${r.height},${shape.color},${shape.layer}"
            }
            ShapeKind.CIRCLE -> {
                val c = shape as Circle
                "circle,${c.radius},${shape.color},${shape.layer}"
            }
            ShapeKind.TRIANGLE -> {
                val t = shape as Triangle
                "triangle,${t.a},${t.b},${t.c},${shape.color},${shape.layer}"
            }
            ShapeKind.CUBE -> {
                val cube = shape as Cube
                "cube,${cube.edge},${shape.color},${shape.layer}"
            }
            ShapeKind.SPHERE -> {
                val s = shape as Sphere
                "sphere,${s.radius},${shape.color},${shape.layer}"
            }
        }
    }
}

class ShapeFactory {
    fun create(spec: String): Shape {
        val parts = spec.split(";")
        val type = parts[0].trim().lowercase()
        val map = parts.drop(1).associate {
            val (k, v) = it.split("=")
            k.trim().lowercase() to v.trim()
        }
        val color = map.getOrDefault("color", "black")
        val layer = map.getOrDefault("layer", "0").toInt()

        return when (type) {
            "rectangle" -> Rectangle(
                map.getOrDefault("width", "0").toDouble(),
                map.getOrDefault("height", "0").toDouble(),
                color, layer
            )
            "circle" -> Circle(
                map.getOrDefault("radius", "0").toDouble(),
                color, layer
            )
            "triangle" -> Triangle(
                map.getOrDefault("a", "0").toDouble(),
                map.getOrDefault("b", "0").toDouble(),
                map.getOrDefault("c", "0").toDouble(),
                color, layer
            )
            "cube" -> Cube(
                map.getOrDefault("edge", "0").toDouble(),
                color, layer
            )
            "sphere" -> Sphere(
                map.getOrDefault("radius", "0").toDouble(),
                color, layer
            )
            else -> throw IllegalArgumentException("Unknown shape type: $type")
        }
    }
}

class ShapePricing {
    private val calc = MetricCalculator()
    fun price(shape: Shape): Double = when (shape.kind) {
        ShapeKind.RECTANGLE -> 0.5 * calc.compute(shape, Metric.AREA) + shape.layer * 2
        ShapeKind.CIRCLE -> 0.6 * calc.compute(shape, Metric.AREA) + 0.1 * calc.compute(shape, Metric.DIAGONAL)
        ShapeKind.TRIANGLE -> 0.8 * calc.compute(shape, Metric.PERIMETER)
        ShapeKind.CUBE -> 1.2 * calc.compute(shape, Metric.VOLUME)
        ShapeKind.SPHERE -> 1.0 * calc.compute(shape, Metric.VOLUME) + 10
    }
}

class LegendBuilder {
    fun legendSymbol(shape: Shape): String = when (shape.kind) {
        ShapeKind.RECTANGLE -> "▭"
        ShapeKind.CIRCLE -> "◯"
        ShapeKind.TRIANGLE -> "△"
        ShapeKind.CUBE -> "⬛"
        ShapeKind.SPHERE -> "◯"
    }
}

class CollisionEngine {
    fun boundingBox(shape: Shape): BoundingBox = when (shape.kind) {
        ShapeKind.RECTANGLE -> {
            val r = shape as Rectangle
            BoundingBox(r.width, r.height)
        }
        ShapeKind.CIRCLE -> {
            val c = shape as Circle
            val d = 2 * c.radius
            BoundingBox(d, d)
        }
        ShapeKind.TRIANGLE -> {
            val t = shape as Triangle
            val max = maxOf(t.a, t.b, t.c)
            val avg = (t.a + t.b + t.c - max) / 2.0
            BoundingBox(max, avg)
        }
        ShapeKind.CUBE -> {
            val cube = shape as Cube
            BoundingBox(cube.edge, cube.edge)
        }
        ShapeKind.SPHERE -> {
            val s = shape as Sphere
            val d = 2 * s.radius
            BoundingBox(d, d)
        }
    }

    fun overlaps(a: Shape, b: Shape): Boolean {
        val A = boundingBox(a)
        val B = boundingBox(b)
        return A.width * A.height > 0 && B.width * B.height > 0
    }
}

class ShapeReport {
    private val calc = MetricCalculator()
    private val ser = ShapeSerializer()
    private val rnd = ShapeRenderer()
    private val col = CollisionEngine()
    private val price = ShapePricing()
    private val legend = LegendBuilder()

    fun summarize(shapes: List<Shape>): String {
        val sb = StringBuilder("=== Shape Report (OCP-violating) ===\n")
        for (s in shapes) {
            sb.append("ID ${s.id} ${s.kind} color=${s.color} layer=${s.layer}\n")
            sb.append("  CSV: ${ser.toCsvRow(s)}\n")
            sb.append("  SVG: ${rnd.renderPseudoSvg(s)}\n")
            sb.append("  Legend: ${legend.legendSymbol(s)}\n")
            sb.append("  Metrics: area=${calc.compute(s, Metric.AREA)} perim=${calc.compute(s, Metric.PERIMETER)} diag=${calc.compute(s, Metric.DIAGONAL)}")
            if (s is ThreeDShape) sb.append(" vol=${calc.compute(s, Metric.VOLUME)}")
            sb.append("\n")
            sb.append("  BBox: ${col.boundingBox(s)}\n")
            sb.append("  Price: ${price.price(s)}\n\n")
        }
        return sb.toString()
    }
}

