import kotlin.math.*

abstract class Entity {
    companion object {
        private var NEXT_ID = 1L
    }
    private val id: Long
    init { this.id = NEXT_ID++ }
    fun getId(): Long = id
}

abstract class GraphicObject(private val color: String, private val layer: Int) : Entity() {
    fun getColor(): String = color
    fun getLayer(): Int = layer
}

enum class ShapeKind { RECTANGLE, CIRCLE, TRIANGLE, CUBE, SPHERE }

data class BoundingBox(val width: Double, val height: Double) {
    override fun toString(): String = "BBox(${width}x${height})"
}

interface HasWidthHeight { fun getWidth(): Double; fun getHeight(): Double }
interface HasRadius { fun getRadius(): Double }
interface HasTriangleSides { fun getA(): Double; fun getB(): Double; fun getC(): Double }
interface HasEdge { fun getEdge(): Double }

abstract class Shape(private val kind: ShapeKind, color: String, layer: Int) : GraphicObject(color, layer) {
    fun getKind(): ShapeKind = kind
    abstract fun isValid(): Boolean
    abstract fun renderPseudoSvg(): String
    abstract fun toCsvRow(): String
    abstract fun legendSymbol(): String
    abstract fun boundingBox(): BoundingBox
    abstract fun price(calc: MetricCalculator): Double
}

abstract class TwoDShape(kind: ShapeKind, color: String, layer: Int) : Shape(kind, color, layer)
abstract class ThreeDShape(kind: ShapeKind, color: String, layer: Int) : Shape(kind, color, layer)

class Rectangle(private val width: Double, private val height: Double, color: String, layer: Int) 
    : TwoDShape(ShapeKind.RECTANGLE, color, layer), HasWidthHeight {
    override fun getWidth(): Double = width
    override fun getHeight(): Double = height
    override fun isValid(): Boolean = width > 0 && height > 0
    override fun renderPseudoSvg(): String = "<rect w=\"$width\" h=\"$height\" fill=\"${getColor()}\" layer=\"${getLayer()}\"/>"
    override fun toCsvRow(): String = "rectangle,$width,$height,${getColor()},${getLayer()}"
    override fun legendSymbol(): String = "▭"
    override fun boundingBox(): BoundingBox = BoundingBox(width, height)
    override fun price(calc: MetricCalculator): Double {
        val area = calc.compute(this, "area")
        return 0.5 * area + getLayer() * 2
    }
}

class Circle(private val radius: Double, color: String, layer: Int) 
    : TwoDShape(ShapeKind.CIRCLE, color, layer), HasRadius {
    override fun getRadius(): Double = radius
    override fun isValid(): Boolean = radius > 0
    override fun renderPseudoSvg(): String = "<circle r=\"$radius\" fill=\"${getColor()}\" layer=\"${getLayer()}\"/>"
    override fun toCsvRow(): String = "circle,$radius,${getColor()},${getLayer()}"
    override fun legendSymbol(): String = "◯"
    override fun boundingBox(): BoundingBox {
        val d = 2 * radius
        return BoundingBox(d, d)
    }
    override fun price(calc: MetricCalculator): Double {
        val area = calc.compute(this, "area")
        val diag = calc.compute(this, "diagonal")
        return 0.6 * area + 0.1 * diag
    }
}

class Triangle(private val a: Double, private val b: Double, private val c: Double, color: String, layer: Int) 
    : TwoDShape(ShapeKind.TRIANGLE, color, layer), HasTriangleSides {
    override fun getA(): Double = a
    override fun getB(): Double = b
    override fun getC(): Double = c
    override fun isValid(): Boolean = a > 0 && b > 0 && c > 0 && a + b > c && a + c > b && b + c > a
    override fun renderPseudoSvg(): String = "<polygon a=\"$a\" b=\"$b\" c=\"$c\" fill=\"${getColor()}\" layer=\"${getLayer()}\"/>"
    override fun toCsvRow(): String = "triangle,$a,$b,$c,${getColor()},${getLayer()}"
    override fun legendSymbol(): String = "△"
    override fun boundingBox(): BoundingBox {
        val max = maxOf(a, b, c)
        val avg = (a + b + c - max) / 2.0
        return BoundingBox(max, avg)
    }
    override fun price(calc: MetricCalculator): Double {
        val per = calc.compute(this, "perimeter")
        return per * 0.8
    }
}

class Cube(private val edge: Double, color: String, layer: Int) 
    : ThreeDShape(ShapeKind.CUBE, color, layer), HasEdge {
    override fun getEdge(): Double = edge
    override fun isValid(): Boolean = edge > 0
    override fun renderPseudoSvg(): String = "<cube edge=\"$edge\" materialColor=\"${getColor()}\" layer=\"${getLayer()}\"/>"
    override fun toCsvRow(): String = "cube,$edge,${getColor()},${getLayer()}"
    override fun legendSymbol(): String = "⬛"
    override fun boundingBox(): BoundingBox = BoundingBox(edge, edge)
    override fun price(calc: MetricCalculator): Double {
        val vol = calc.compute(this, "volume")
        return 1.2 * vol
    }
}

class Sphere(private val radius: Double, color: String, layer: Int) 
    : ThreeDShape(ShapeKind.SPHERE, color, layer), HasRadius {
    override fun getRadius(): Double = radius
    override fun isValid(): Boolean = radius > 0
    override fun renderPseudoSvg(): String = "<sphere r=\"$radius\" materialColor=\"${getColor()}\" layer=\"${getLayer()}\"/>"
    override fun toCsvRow(): String = "sphere,$radius,${getColor()},${getLayer()}"
    override fun legendSymbol(): String = "◯"
    override fun boundingBox(): BoundingBox {
        val d = 2 * radius
        return BoundingBox(d, d)
    }
    override fun price(calc: MetricCalculator): Double {
        val vol = calc.compute(this, "volume")
        return 1.0 * vol + 10
    }
}

class RegisteredMetric(private val name: String) {
    private val handlers = linkedMapOf<Class<*>, (Any) -> Double>()
    fun name(): String = name
    fun <T> on(type: Class<T>, fn: (T) -> Double): RegisteredMetric {
        handlers[type] = { o -> fn(type.cast(o)) }
        return this
    }
    fun apply(s: Shape): Double {
        for ((key, value) in handlers) {
            if (key.isInstance(s)) return value(s)
        }
        return 0.0
    }
}

class MetricCalculator {
    private val metrics = hashMapOf<String, RegisteredMetric>()
    fun register(metric: RegisteredMetric) { metrics[metric.name()] = metric }
    fun compute(shape: Shape, metricName: String): Double {
        val m = metrics[metricName] ?: throw IllegalArgumentException("Unknown metric: $metricName")
        return m.apply(shape)
    }
}

class ShapeValidator { fun isValid(shape: Shape): Boolean = shape.isValid() }
class ShapeRenderer { fun renderPseudoSvg(shape: Shape): String = shape.renderPseudoSvg() }
class ShapeSerializer { fun toCsvRow(shape: Shape): String = shape.toCsvRow() }

class ShapeFactory {
    fun interface Creator { fun create(map: Map<String, String>, color: String, layer: Int): Shape }
    private val creators = hashMapOf<String, Creator>()
    fun register(type: String, c: Creator) { creators[type.lowercase()] = c }
    fun create(spec: String): Shape {
        val parts = spec.split(";")
        val type = parts[0].trim().lowercase()
        val map = hashMapOf<String, String>()
        for (i in 1 until parts.size) {
            val kv = parts[i].split("=")
            if (kv.size == 2) map[kv[0].trim().lowercase()] = kv[1].trim()
        }
        val color = map.getOrDefault("color", "black")
        val layer = map.getOrDefault("layer", "0").toInt()
        val c = creators[type] ?: throw IllegalArgumentException("Unknown shape type: $type")
        return c.create(map, color, layer)
    }
}

class ShapePricing(private val calc: MetricCalculator) {
    fun price(shape: Shape): Double = shape.price(calc)
}

class LegendBuilder { fun legendSymbol(shape: Shape): String = shape.legendSymbol() }

class CollisionEngine {
    fun boundingBox(shape: Shape): BoundingBox = shape.boundingBox()
    fun overlaps(a: Shape, b: Shape): Boolean {
        val A = boundingBox(a)
        val B = boundingBox(b)
        return (A.width * A.height) > 0 && (B.width * B.height) > 0
    }
}

class ShapeReport(private val calc: MetricCalculator) {
    private val ser = ShapeSerializer()
    private val rnd = ShapeRenderer()
    private val col = CollisionEngine()
    private val price = ShapePricing(calc)
    private val legend = LegendBuilder()

    fun summarize(shapes: List<Shape>): String {
        val sb = StringBuilder()
        sb.append("=== Shape Report (OCP-violating) ===\n")
        for (s in shapes) {
            sb.append("ID ").append(s.getId())
              .append(" ").append(s.getKind())
              .append(" color=").append(s.getColor())
              .append(" layer=").append(s.getLayer()).append("\n")

            sb.append("  CSV: ").append(ser.toCsvRow(s)).append("\n")
            sb.append("  SVG: ").append(rnd.renderPseudoSvg(s)).append("\n")
            sb.append("  Legend: ").append(legend.legendSymbol(s)).append("\n")

            sb.append("  Metrics: ")
            sb.append(" area=").append(calc.compute(s, "area"))
            sb.append(" perim=").append(calc.compute(s, "perimeter"))
            sb.append(" diag=").append(calc.compute(s, "diagonal"))
            if (s is ThreeDShape) {
                sb.append(" vol=").append(calc.compute(s, "volume"))
            }
            sb.append("\n")

            sb.append("  BBox: ").append(col.boundingBox(s)).append("\n")
            sb.append("  Price: ").append(price.price(s)).append("\n\n")
        }
        return sb.toString()
    }
}

