import java.util.*

enum class CharacterType { WARRIOR, MAGE, ARCHER, ROGUE }
enum class SkillType { BASIC, SPECIAL, SUPPORT }
enum class WeaponType { SWORD, STAFF, BOW, DAGGER }
enum class PetType { WOLF, FAIRY, HAWK, SHADOW_CAT }

data class Stats(var hp: Int, var mp: Int, var atk: Int, var mag: Int, var def: Int, var res: Int, var spd: Int, var crit: Int) {
    fun copy() = Stats(hp, mp, atk, mag, def, res, spd, crit)
}

data class Skill(val name: String, val type: SkillType, val cost: Int)
data class Weapon(val name: String, val type: WeaponType, val power: Int)
data class Pet(val name: String, val type: PetType, val bond: Int)
data class Enemy(val name: String, val stats: Stats)

data class GameCharacter(
    val name: String,
    val type: CharacterType,
    val level: Int,
    val stats: Stats,
    val skills: List<Skill>,
    val weapon: Weapon,
    val pet: Pet
)

class PetBoostSystem {
    fun offenseBoost(c: GameCharacter): Int = when (c.pet.type) {
        PetType.WOLF -> (c.stats.atk * minOf(0.25, 0.05 + c.pet.bond / 100.0)).toInt()
        PetType.FAIRY -> (c.stats.mag * minOf(0.25, 0.05 + c.pet.bond / 120.0)).toInt()
        PetType.HAWK -> c.stats.spd / 3 + c.pet.bond / 6
        PetType.SHADOW_CAT -> 5 + c.pet.bond / 5
    }

    fun defenseBoost(c: GameCharacter): Int = when (c.pet.type) {
        PetType.WOLF -> c.stats.def / 6
        PetType.FAIRY -> c.stats.res / 4
        PetType.HAWK -> c.stats.spd / 6
        PetType.SHADOW_CAT -> 3
    }

    fun petAssist(c: GameCharacter): Boolean = when (c.pet.type) {
        PetType.WOLF -> Random().nextInt(100) < minOf(35, 10 + c.pet.bond / 3)
        PetType.FAIRY -> Random().nextInt(100) < minOf(40, 15 + c.pet.bond / 2)
        PetType.HAWK -> Random().nextInt(100) < minOf(30, 8 + c.pet.bond / 4)
        PetType.SHADOW_CAT -> Random().nextInt(100) < minOf(45, 12 + c.pet.bond / 2)
    }

    fun petAssistDamage(c: GameCharacter): Int = when (c.pet.type) {
        PetType.WOLF -> 6 + c.pet.bond / 4
        PetType.FAIRY -> 4 + c.pet.bond / 5
        PetType.HAWK -> 5 + c.pet.bond / 6
        PetType.SHADOW_CAT -> 7 + c.pet.bond / 5
    }

    fun petAssistHeal(c: GameCharacter): Int = when (c.pet.type) {
        PetType.FAIRY -> 8 + c.pet.bond / 3
        else -> 0
    }
}

class WeaponRuleSet {
    fun applyWeapon(c: GameCharacter, base: Int): Int = when (c.weapon.type) {
        WeaponType.SWORD -> base + c.weapon.power + c.stats.atk / 4
        WeaponType.STAFF -> base + c.weapon.power / 2 + c.stats.mag / 2
        WeaponType.BOW -> base + c.weapon.power + c.stats.spd / 3
        WeaponType.DAGGER -> base + c.weapon.power + c.stats.spd / 2
    }

    fun critChance(c: GameCharacter): Int = when (c.weapon.type) {
        WeaponType.SWORD -> 8 + c.stats.crit
        WeaponType.STAFF -> 5 + c.stats.crit / 2
        WeaponType.BOW -> 15 + c.stats.crit
        WeaponType.DAGGER -> 18 + c.stats.crit
    }
}

class DamageCalculator {
    private val rng = Random()
    private val wr = WeaponRuleSet()
    private val pets = PetBoostSystem()

    fun calc(c: GameCharacter, s: Skill, e: Enemy): Int {
        var base = when (c.type) {
            CharacterType.WARRIOR -> (c.stats.atk * (1.1 + c.level * 0.03)).toInt() - e.stats.def / 3 + if (s.type == SkillType.SPECIAL) 12 else 0
            CharacterType.MAGE -> c.stats.mag * 2 + if (s.type == SkillType.SPECIAL) 18 else 0 - e.stats.res / 2
            CharacterType.ARCHER -> c.stats.atk + c.stats.spd / 2 + if (s.type == SkillType.SPECIAL) 10 else 0 - e.stats.def / 4
            CharacterType.ROGUE -> c.stats.atk + c.stats.spd + if (s.type == SkillType.SPECIAL) 14 else 0 - e.stats.def / 5
        }
        base = wr.applyWeapon(c, base)
        base += pets.offenseBoost(c)
        if (pets.petAssist(c)) base += pets.petAssistDamage(c)
        val critChance = wr.critChance(c) + if (c.type == CharacterType.ARCHER) 10 else 0
        if (rng.nextInt(100) < critChance) base *= 2
        return maxOf(0, base)
    }
}

class HealCalculator {
    private val pets = PetBoostSystem()
    fun heal(c: GameCharacter, s: Skill): Int {
        var amt = when (c.type) {
            CharacterType.WARRIOR -> c.stats.atk / 3 + 6
            CharacterType.MAGE -> c.stats.mag + 14
            CharacterType.ARCHER -> c.stats.atk / 4 + c.stats.spd / 5
            CharacterType.ROGUE -> c.stats.atk / 2
        }
        if (s.type == SkillType.SPECIAL) amt += 6
        amt += pets.defenseBoost(c) / 2 + pets.petAssistHeal(c)
        return maxOf(0, amt)
    }
}

class LootCalculator {
    fun gold(c: GameCharacter, e: Enemy): Int = when (c.type) {
        CharacterType.WARRIOR -> 10 + c.level + e.stats.def / 5
        CharacterType.MAGE -> 9 + c.level * 2 + e.stats.res / 4
        CharacterType.ARCHER -> 11 + c.level + e.stats.def / 6
        CharacterType.ROGUE -> 14 + c.level
    }
}

class TargetingSystem {
    fun pick(enemies: List<Enemy>): Enemy {
        var t = enemies[0]
        for (e in enemies) if (e.stats.hp < t.stats.hp) t = e
        return t
    }
}

class SkillSelector {
    fun choose(c: GameCharacter): Skill = c.skills.firstOrNull { it.type != SkillType.BASIC } ?: c.skills[0]
}

class TurnEngine {
    private val dmg = DamageCalculator()
    private val healCalc = HealCalculator()
    private val loot = LootCalculator()
    private val targeting = TargetingSystem()
    private val selector = SkillSelector()

    fun turn(party: List<GameCharacter>, enemies: MutableList<Enemy>) {
        for (c in party) {
            val s = selector.choose(c)
            if (s.type == SkillType.SUPPORT) {
                val h = healCalc.heal(c, s)
                c.stats.hp += h
                continue
            }
            val target = targeting.pick(enemies)
            val d = dmg.calc(c, s, target)
            target.stats.hp = maxOf(0, target.stats.hp - d)
        }
    }

    fun totalGold(party: List<GameCharacter>, e: Enemy): Int = party.sumOf { loot.gold(it, e) }
}

