        put(PetType.HAWK, HawkPetLogic())
        put(PetType.SHADOW_CAT, ShadowCatPetLogic())
    }
    fun of(t: PetType): PetLogic = map[t]!!
}

interface WeaponLogic {
    fun applyWeapon(c: GameCharacter, base: Int): Int
    fun critChance(c: GameCharacter): Int
}

class SwordLogic : WeaponLogic {
    override fun applyWeapon(c: GameCharacter, base: Int): Int {
        return base + c.weapon.power + c.stats.atk / 4
    }
    override fun critChance(c: GameCharacter): Int {
        return 8 + c.stats.crit
    }
}

class StaffLogic : WeaponLogic {
    override fun applyWeapon(c: GameCharacter, base: Int): Int {
        return base + c.weapon.power / 2 + c.stats.mag / 2
    }
    override fun critChance(c: GameCharacter): Int {
        return 5 + c.stats.crit / 2
    }
}

class BowLogic : WeaponLogic {
    override fun applyWeapon(c: GameCharacter, base: Int): Int {
        return base + c.weapon.power + c.stats.spd / 3
    }
    override fun critChance(c: GameCharacter): Int {
        return 15 + c.stats.crit
    }
}

class DaggerLogic : WeaponLogic {
    override fun applyWeapon(c: GameCharacter, base: Int): Int {
        return base + c.weapon.power + c.stats.spd / 2
    }
    override fun critChance(c: GameCharacter): Int {
        return 18 + c.stats.crit
    }
}

object WeaponLogicRegistry {
    private val map: EnumMap<WeaponType, WeaponLogic> = EnumMap<WeaponType, WeaponLogic>(WeaponType::class.java).apply {
        put(WeaponType.SWORD, SwordLogic())
        put(WeaponType.STAFF, StaffLogic())
        put(WeaponType.BOW, BowLogic())
        put(WeaponType.DAGGER, DaggerLogic())
    }
    fun of(t: WeaponType): WeaponLogic = map[t]!!
}

interface RoleLogic {
    fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int
    fun baseHeal(c: GameCharacter, s: Skill): Int
    fun gold(c: GameCharacter, e: Enemy): Int
    fun extraCrit(c: GameCharacter): Int
}

class WarriorLogic : RoleLogic {
    override fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int {
        var base = (c.stats.atk * (1.1 + c.level * 0.03)).toInt() - e.stats.def / 3
        if (s.type == SkillType.SPECIAL) base += 12
        return base
    }
    override fun baseHeal(c: GameCharacter, s: Skill): Int {
        var amt = c.stats.atk / 3 + 6
        if (s.type == SkillType.SPECIAL) amt += 6
        return amt
    }
    override fun gold(c: GameCharacter, e: Enemy): Int {
        return 10 + c.level + e.stats.def / 5
    }
    override fun extraCrit(c: GameCharacter): Int = 0
}

class MageLogic : RoleLogic {
    override fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int {
        val base = c.stats.mag * 2 + if (s.type == SkillType.SPECIAL) 18 else 0 - e.stats.res / 2
        return base
    }
    override fun baseHeal(c: GameCharacter, s: Skill): Int {
        var amt = c.stats.mag + 14
        if (s.type == SkillType.SPECIAL) amt += 6
        return amt
    }
    override fun gold(c: GameCharacter, e: Enemy): Int {
        return 9 + c.level * 2 + e.stats.res / 4
    }
    override fun extraCrit(c: GameCharacter): Int = 0
}

class ArcherLogic : RoleLogic {
    override fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int {
        val base = c.stats.atk + c.stats.spd / 2 + if (s.type == SkillType.SPECIAL) 10 else 0 - e.stats.def / 4
        return base
    }
    override fun baseHeal(c: GameCharacter, s: Skill): Int {
        var amt = c.stats.atk / 4 + c.stats.spd / 5
        if (s.type == SkillType.SPECIAL) amt += 6
        return amt
    }
    override fun gold(c: GameCharacter, e: Enemy): Int {
        return 11 + c.level + e.stats.def / 6
    }
    override fun extraCrit(c: GameCharacter): Int = 10
}

class RogueLogic : RoleLogic {
    override fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int {
        val base = c.stats.atk + c.stats.spd + if (s.type == SkillType.SPECIAL) 14 else 0 - e.stats.def / 5
        return base
    }
    override fun baseHeal(c: GameCharacter, s: Skill): Int {
        var amt = c.stats.atk / 2
        if (s.type == SkillType.SPECIAL) amt += 6
        return amt
    }
    override fun gold(c: GameCharacter, e: Enemy): Int {
        return 14 + c.level
    }
    override fun extraCrit(c: GameCharacter): Int = 0
}

object RoleLogicRegistry {
    private val map: EnumMap<CharacterType, RoleLogic> = EnumMap<CharacterType, RoleLogic>(CharacterType::class.java).apply {
        put(CharacterType.WARRIOR, WarriorLogic())
        put(CharacterType.MAGE, MageLogic())
        put(CharacterType.ARCHER, ArcherLogic())
        put(CharacterType.ROGUE, RogueLogic())
    }
    fun of(t: CharacterType): RoleLogic = map[t]!!
}

data class GameCharacter(
    val name: String,
    val type: CharacterType,
    val level: Int,
    val stats: Stats,
    val skills: List<Skill>,
    val weapon: Weapon,
    val pet: Pet
)

class PetBoostSystem {
    fun offenseBoost(c: GameCharacter): Int {
        return PetLogicRegistry.of(c.pet.type).offenseBoost(c)
    }
    fun defenseBoost(c: GameCharacter): Int {
        return PetLogicRegistry.of(c.pet.type).defenseBoost(c)
    }
    fun petAssist(c: GameCharacter): Boolean {
        return PetLogicRegistry.of(c.pet.type).assist(c)
    }
    fun petAssistDamage(c: GameCharacter): Int {
        return PetLogicRegistry.of(c.pet.type).assistDamage(c)
    }
    fun petAssistHeal(c: GameCharacter): Int {
        return PetLogicRegistry.of(c.pet.type).assistHeal(c)
    }
}

class WeaponRuleSet {
    fun applyWeapon(c: GameCharacter, base: Int): Int {
        return WeaponLogicRegistry.of(c.weapon.type).applyWeapon(c, base)
    }
    fun critChance(c: GameCharacter): Int {
        return WeaponLogicRegistry.of(c.weapon.type).critChance(c)
    }
}

class RoleRuleSet {
    fun baseDamage(c: GameCharacter, s: Skill, e: Enemy): Int {
        return RoleLogicRegistry.of(c.type).baseDamage(c, s, e)
    }
    fun baseHeal(c: GameCharacter, s: Skill): Int {
        return RoleLogicRegistry.of(c.type).baseHeal(c, s)
    }
    fun gold(c: GameCharacter, e: Enemy): Int {
        return RoleLogicRegistry.of(c.type).gold(c, e)
    }
    fun extraCrit(c: GameCharacter): Int {
        return RoleLogicRegistry.of(c.type).extraCrit(c)
    }
}

class DamageCalculator {
    private val rng = Random()
    private val wr = WeaponRuleSet()
    private val pets = PetBoostSystem()
    private val roles = RoleRuleSet()

    fun calc(c: GameCharacter, s: Skill, e: Enemy): Int {
        var base = roles.baseDamage(c, s, e)
        base = wr.applyWeapon(c, base)
        base += pets.offenseBoost(c)
        if (pets.petAssist(c)) base += pets.petAssistDamage(c)
        val critChance = wr.critChance(c) + roles.extraCrit(c)
        if (rng.nextInt(100) < critChance) base *= 2
        return kotlin.math.max(0, base)
    }
}

class HealCalculator {
    private val pets = PetBoostSystem()
    private val roles = RoleRuleSet()
    fun heal(c: GameCharacter, s: Skill): Int {
        var amt = roles.baseHeal(c, s)
        amt += pets.defenseBoost(c) / 2 + pets.petAssistHeal(c)
        return kotlin.math.max(0, amt)
    }
}

class LootCalculator {
    private val roles = RoleRuleSet()
    fun gold(c: GameCharacter, e: Enemy): Int {
        return roles.gold(c, e)
    }
}

class TargetingSystem {
    fun pick(enemies: List<Enemy>): Enemy {
        var t = enemies[0]
        for (e in enemies) if (e.stats.hp < t.stats.hp) t = e
        return t
    }
}

class SkillSelector {
    fun choose(c: GameCharacter): Skill {
        for (s in c.skills) if (s.type != SkillType.BASIC) return s
        return c.skills[0]
    }
}

class TurnEngine {
    private val dmg = DamageCalculator()
    private val healCalc = HealCalculator()
    private val loot = LootCalculator()
    private val targeting = TargetingSystem()
    private val selector = SkillSelector()

    fun turn(party: List<GameCharacter>, enemies: MutableList<Enemy>) {
        for (c in party) {
            val s = selector.choose(c)
            if (s.type == SkillType.SUPPORT) {
                val h = healCalc.heal(c, s)
                c.stats.hp += h
                continue
            }
            val target = targeting.pick(enemies)
            val d = dmg.calc(c, s, target)
            target.stats.hp = kotlin.math.max(0, target.stats.hp - d)
        }
    }
    fun totalGold(party: List<GameCharacter>, e: Enemy): Int {
        var g = 0
        for (c in party) g += loot.gold(c, e)
        return g
    }
}

