        stock["dough"] = 30; stock["soda"] = 100; stock["greens"] = 40; stock["dessertBase"] = 30
    }
    fun reserve(item: FoodItem): Boolean = when (item.getType()) {
        FoodType.BURGER -> take("bun",1) && take("patty",1)
        FoodType.FRIES -> take("potato",2)
        FoodType.PIZZA -> take("dough",1)
        FoodType.DRINK -> take("soda",1)
        FoodType.SALAD -> take("greens",1)
        FoodType.DESSERT -> take("dessertBase",1)
