class Cashier {
    private val prices = PriceCalculator()
    private val taxes = TaxCalculator()
    private val promos = PromotionEngine()
    fun total(order: Order): Double {
        var sum = 0.0
        for (item in order.items()) {
            val base = prices.price(item)
            val discount = promos.discount(item)
            val taxed = (base - discount) * (1.0 + taxes.taxFor(item))
            sum += taxed
        }
        return Math.round(sum * 100.0) / 100.0
    }
}

