import java.util.*

enum class FoodType { BURGER, FRIES, PIZZA, DRINK, SALAD, DESSERT }
enum class Size { SMALL, MEDIUM, LARGE }

class FoodItem(
    private val name: String,
    private val type: FoodType,
    private val size: Size,
    extras: MutableSet<String>?
) {
    private val extras: MutableSet<String> = extras ?: HashSet()
    private val strategy: FoodStrategy = FoodStrategyFactory.create(type)
    fun getName(): String = name
    fun getType(): FoodType = type
    fun getSize(): Size = size
    fun getExtras(): MutableSet<String> = extras
    fun getStrategy(): FoodStrategy = strategy
}

class Order(private val customer: String) {
    private val items: MutableList<FoodItem> = ArrayList()
    fun add(item: FoodItem) { items.add(item) }
    fun items(): MutableList<FoodItem> = items
    fun customer(): String = customer
}

interface FoodStrategy {
    fun getPrice(size: Size): Double
    fun getTaxRate(): Double
    fun getCalories(size: Size): Int
    fun getDiscount(item: FoodItem): Double
    fun getStation(): String
    fun reserveInventory(stock: MutableMap<String, Int>): Boolean
    fun prepare()
    fun serve()
    fun getPackaging(size: Size): String
    fun getAllergens(item: FoodItem): MutableSet<String>
    fun getCourier(): String
    fun getLoyaltyPoints(): Int
    fun getPrepWasteGrams(): Int
    fun getStationLoadWeight(): Int
    fun passQuality(item: FoodItem): Boolean
    fun getCookingSeconds(size: Size): Int
    fun isAvailable(hour: Int): Boolean
    fun getSpiceLevel(item: FoodItem): Int
    fun getUpsellSuggestion(): String
}

abstract class BaseFoodStrategy : FoodStrategy {
    protected fun takeFromStock(stock: MutableMap<String, Int>, key: String, qty: Int): Boolean {
        val have = stock.getOrDefault(key, 0)
        if (have < qty) return false
        stock[key] = have - qty
        return true
    }
}

class BurgerStrategy : BaseFoodStrategy() {
    override fun getPrice(size: Size): Double {
        return when (size) {
            Size.SMALL -> 5.49
            Size.MEDIUM -> 6.49
            else -> 7.49
        }
    }
    override fun getTaxRate(): Double = 0.05
    override fun getCalories(size: Size): Int {
        return when (size) {
            Size.SMALL -> 480
            Size.MEDIUM -> 650
            else -> 820
        }
    }
    override fun getDiscount(item: FoodItem): Double {
        return if (item.getExtras().contains("extra-cheese")) 0.50 else 0.0
    }
    override fun getStation(): String = "Grill"
    override fun reserveInventory(stock: MutableMap<String, Int>): Boolean {
        return takeFromStock(stock, "bun", 1) && takeFromStock(stock, "patty", 1)
    }
    override fun prepare() {
        println("Chef grills patty and assembles burger")
    }
    override fun serve() {
        println("Waiter serves burger with napkins")
    }
    override fun getPackaging(size: Size): String {
