class Cashier {
    private val prices = PriceCalculator()
    private val taxes = TaxCalculator()
    private val promos = PromotionEngine()
    fun total(order: Order): Double {
        var sum = 0.0
        for (item in order.items()) {
            val base = prices.price(item)
            val discount = promos.discount(item)
            val taxed = (base - discount) * (1.0 + taxes.taxFor(item))
            sum += taxed
        }
        return Math.round(sum * 100.0) / 100.0
    }
}

class ReceiptPrinter {
    private val prices = PriceCalculator()
    private val taxes = TaxCalculator()
    private val promos = PromotionEngine()
    private val nutrition = NutritionCalculator()
    fun print(order: Order) {
        println("Customer: " + order.customer())
        for (item in order.items()) {
            val base = prices.price(item)
            val discount = promos.discount(item)
            val taxRate = taxes.taxFor(item)
            val kcal = nutrition.calories(item)
            println(item.getName() + " (" + item.getType() + " " + item.getSize() + ") base $" + base + " disc $" + discount + " tax " + (taxRate * 100).toInt() + "% kcal " + kcal)
        }
    }
}

