import java.util.*

enum class DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER, BLIND, SPEAKER }
enum class RoomType { LIVING, KITCHEN, BEDROOM, BATHROOM, GARDEN, GARAGE }
enum class Mode { ECO, COMFORT, AWAY, NIGHT, PARTY }
enum class AlertType { INFO, WARN, CRITICAL }

class SmartDevice(private val id: String, private val type: DeviceType) {
    private var mode: Mode = Mode.ECO
    private var online: Boolean = true
    fun getId() = id
    fun getType() = type
    fun getMode() = mode
    fun isOnline() = online
    fun setMode(m: Mode) { mode = m }
    fun setOnline(o: Boolean) { online = o }
}

class Room(private val name: String, private val type: RoomType) {
    private val devices: MutableList<SmartDevice> = ArrayList()
    fun add(d: SmartDevice) { devices.add(d) }
    fun getDevices() = devices
    fun getName() = name
    fun getType() = type
}

class SmartHomeController {
    fun turnOn(d: SmartDevice) {
        when (d.getType()) {
            DeviceType.LIGHT -> println("Light ${d.getId()} ON")
            DeviceType.THERMOSTAT -> println("Thermostat ${d.getId()} heating")
            DeviceType.DOOR_LOCK -> println("Lock ${d.getId()} locked")
            DeviceType.CAMERA -> println("Camera ${d.getId()} recording")
            DeviceType.SPRINKLER -> println("Sprinkler ${d.getId()} watering")
            DeviceType.BLIND -> println("Blind ${d.getId()} open")
            DeviceType.SPEAKER -> println("Speaker ${d.getId()} playing")
        }
    }
    fun turnOff(d: SmartDevice) {
        when (d.getType()) {
            DeviceType.LIGHT -> println("Light ${d.getId()} OFF")
            DeviceType.THERMOSTAT -> println("Thermostat ${d.getId()} idle")
            DeviceType.DOOR_LOCK -> println("Lock ${d.getId()} secured")
            DeviceType.CAMERA -> println("Camera ${d.getId()} standby")
            DeviceType.SPRINKLER -> println("Sprinkler ${d.getId()} stopped")
            DeviceType.BLIND -> println("Blind ${d.getId()} closed")
            DeviceType.SPEAKER -> println("Speaker ${d.getId()} paused")
        }
    }
    fun setMode(d: SmartDevice, m: Mode) {
        d.setMode(m)
        println("${d.getType().name.lowercase().replaceFirstChar { it.uppercase() }} ${d.getId()} -> $m")
    }
    fun energyUsageKwh(d: SmartDevice, minutes: Int): Double = when (d.getType()) {
        DeviceType.LIGHT -> 0.012 * minutes
        DeviceType.THERMOSTAT -> 0.08 * minutes
        DeviceType.DOOR_LOCK -> 0.0002 * minutes
        DeviceType.CAMERA -> 0.01 * minutes
        DeviceType.SPRINKLER -> 0.03 * minutes
        DeviceType.BLIND -> if (minutes == 0) 0.0 else 0.001 * minutes
        DeviceType.SPEAKER -> 0.015 * minutes
    }
}

class NotificationService {
    fun push(d: SmartDevice, t: AlertType, msg: String) {
        println("[$t] ${d.getType().name.lowercase().replaceFirstChar { it.uppercase() }} ${d.getId()}: $msg")
    }
}

class DiagnosticsService {
    fun health(d: SmartDevice): String = when (d.getType()) {
        DeviceType.LIGHT -> if (d.isOnline()) "OK" else "Bulb Offline"
        DeviceType.THERMOSTAT -> if (d.isOnline()) "OK" else "Sensor Offline"
        DeviceType.DOOR_LOCK -> if (d.isOnline()) "OK" else "Battery Low"
        DeviceType.CAMERA -> if (d.isOnline()) "OK" else "Stream Error"
        DeviceType.SPRINKLER -> if (d.isOnline()) "OK" else "Valve Error"
        DeviceType.BLIND -> if (d.isOnline()) "OK" else "Motor Jam"
        DeviceType.SPEAKER -> if (d.isOnline()) "OK" else "Audio Fault"
    }
}

class SecurityPolicy {
    fun allowed(d: SmartDevice, action: String, userHome: Boolean): Boolean = when (d.getType()) {
        DeviceType.DOOR_LOCK -> if (action == "unlock") userHome else true
        DeviceType.CAMERA -> !(action == "disable" && !userHome)
        DeviceType.SPRINKLER -> !(action == "on" && userHome && d.getMode() == Mode.PARTY)
        DeviceType.SPEAKER -> !(action == "play" && !userHome)
        else -> true
    }
}

class VoiceCommandProcessor {
    private val ctl = SmartHomeController()
    fun handle(phrase: String, d: SmartDevice) {
        when (d.getType()) {
            DeviceType.LIGHT -> when (phrase.lowercase()) {
                "lights on" -> ctl.turnOn(d)
                "lights off" -> ctl.turnOff(d)
            }
            DeviceType.THERMOSTAT -> when (phrase.lowercase()) {
                "heat on" -> ctl.turnOn(d)
                "eco" -> ctl.setMode(d, Mode.ECO)
            }
            DeviceType.DOOR_LOCK -> if (phrase.equals("lock door", true)) ctl.turnOn(d)
            DeviceType.CAMERA -> if (phrase.equals("record", true)) ctl.turnOn(d)
            DeviceType.SPRINKLER -> if (phrase.equals("water", true)) ctl.turnOn(d)
            DeviceType.BLIND -> when (phrase.lowercase()) {
                "open blinds" -> ctl.turnOn(d)
                "close blinds" -> ctl.turnOff(d)
            }
            DeviceType.SPEAKER -> when (phrase.lowercase()) {
                "play music" -> ctl.turnOn(d)
                "pause music" -> ctl.turnOff(d)
            }
        }
    }
}

class SceneManager {
    private val ctl = SmartHomeController()
    fun apply(scene: String, devices: List<SmartDevice>) {
        for (d in devices) {
            when (scene) {
                "Evening" -> when (d.getType()) {
                    DeviceType.LIGHT -> { ctl.turnOn(d); ctl.setMode(d, Mode.COMFORT) }
                    DeviceType.THERMOSTAT -> ctl.setMode(d, Mode.COMFORT)
                    DeviceType.CAMERA -> ctl.turnOn(d)
                    DeviceType.DOOR_LOCK -> ctl.turnOn(d)
                    DeviceType.SPRINKLER -> ctl.turnOff(d)
                    DeviceType.BLIND -> ctl.turnOff(d)
                    DeviceType.SPEAKER -> ctl.turnOn(d)
                }
                "Away" -> when (d.getType()) {
                    DeviceType.LIGHT -> ctl.turnOff(d)
                    DeviceType.THERMOSTAT -> ctl.setMode(d, Mode.AWAY)
                    DeviceType.CAMERA -> ctl.turnOn(d)
                    DeviceType.DOOR_LOCK -> ctl.turnOn(d)
                    DeviceType.SPRINKLER -> ctl.turnOff(d)
                    DeviceType.BLIND -> ctl.turnOff(d)
                    DeviceType.SPEAKER -> ctl.turnOff(d)
                }
                "Party" -> when (d.getType()) {
                    DeviceType.LIGHT -> { ctl.turnOn(d); ctl.setMode(d, Mode.PARTY) }
                    DeviceType.THERMOSTAT -> ctl.setMode(d, Mode.COMFORT)
                    DeviceType.SPEAKER -> ctl.turnOn(d)
                    DeviceType.CAMERA -> ctl.turnOff(d)
                    DeviceType.BLIND -> ctl.turnOn(d)
                    DeviceType.SPRINKLER -> ctl.turnOff(d)
                    DeviceType.DOOR_LOCK -> ctl.turnOn(d)
                }
            }
        }
    }
}

class FirmwareManager {
    fun steps(d: SmartDevice): List<String> = when (d.getType()) {
        DeviceType.LIGHT -> listOf("Ping","PushFW","Reboot")
        DeviceType.THERMOSTAT -> listOf("Ping","Backup","PushFW","Recalibrate")
        DeviceType.DOOR_LOCK -> listOf("SecureChannel","PushFW","Reauth")
        DeviceType.CAMERA -> listOf("Ping","PushFW","RestartStream")
        DeviceType.SPRINKLER -> listOf("Ping","PushFW","ValveTest")
        DeviceType.BLIND -> listOf("Ping","PushFW","AlignMotor")
        DeviceType.SPEAKER -> listOf("Ping","PushFW","RestartAudio")
    }
}

class ScheduleEngine {
    class Entry(val device: SmartDevice, val cron: String, val action: String)
    fun run(e: Entry, ctl: SmartHomeController) {
        when (e.device.getType()) {
            DeviceType.LIGHT -> if (e.action == "on") ctl.turnOn(e.device) else ctl.turnOff(e.device)
            DeviceType.SPRINKLER -> if (e.action == "on") ctl.turnOn(e.device) else ctl.turnOff(e.device)
            DeviceType.THERMOSTAT -> if (e.action == "comfort") ctl.setMode(e.device, Mode.COMFORT)
            DeviceType.BLIND -> if (e.action == "open") ctl.turnOn(e.device) else ctl.turnOff(e.device)
            DeviceType.SPEAKER -> if (e.action == "play") ctl.turnOn(e.device) else ctl.turnOff(e.device)
            else -> ctl.turnOff(e.device)
        }
    }
}

