class AutomationRuleEngine {
    private val ctl = SmartHomeController()
    fun onEvent(event: String, d: SmartDevice) {
        when (event) {
            "motion" -> when (d.getType()) {
                DeviceType.CAMERA -> ctl.turnOn(d)
                DeviceType.LIGHT -> ctl.turnOn(d)
                else -> {}
            }
            "sunset" -> when (d.getType()) {
                DeviceType.LIGHT -> ctl.turnOn(d)
                DeviceType.BLIND -> ctl.turnOff(d)
                else -> {}
            }
            "door-open" -> when (d.getType()) {
                DeviceType.DOOR_LOCK -> ctl.turnOn(d)
                DeviceType.CAMERA -> ctl.turnOn(d)
                else -> {}
            }
        }
    }
}

class ReportPrinter {
    private val ctl = SmartHomeController()
    private val diag = DiagnosticsService()
    private val render = DeviceRenderer()
    fun printRoom(room: Room) {
