abstract class GraphicObject(private val color: String) {
    fun getColor(): String = color
}

enum class ShapeKind {
    RECTANGLE, CIRCLE, CUBE
}

abstract class Shape(private val kind: ShapeKind, color: String) : GraphicObject(color) {
    fun getKind(): ShapeKind = kind
}

abstract class TwoDShape(kind: ShapeKind, color: String) : Shape(kind, color)
abstract class ThreeDShape(kind: ShapeKind, color: String) : Shape(kind, color)

class Rectangle(private val width: Double, private val height: Double, color: String) :
    TwoDShape(ShapeKind.RECTANGLE, color) {
    fun getWidth(): Double = width
    fun getHeight(): Double = height
}

class Circle(private val radius: Double, color: String) :
    TwoDShape(ShapeKind.CIRCLE, color) {
    fun getRadius(): Double = radius
}

class Cube(private val edge: Double, color: String) :
    ThreeDShape(ShapeKind.CUBE, color) {
    fun getEdge(): Double = edge
}

enum class Metric { AREA, PERIMETER, VOLUME }

class MetricCalculator {
    fun compute(shape: Shape, metric: Metric): Double {
        return when (shape.getKind()) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                when (metric) {
                    Metric.AREA -> r.getWidth() * r.getHeight()
                    Metric.PERIMETER -> 2 * (r.getWidth() + r.getHeight())
                    Metric.VOLUME -> 0.0
                }
            }
            ShapeKind.CIRCLE -> {
                val c = shape as Circle
                when (metric) {
                    Metric.AREA -> Math.PI * c.getRadius() * c.getRadius()
                    Metric.PERIMETER -> 2 * Math.PI * c.getRadius()
                    Metric.VOLUME -> 0.0
                }
            }
            ShapeKind.CUBE -> {
                val cube = shape as Cube
                when (metric) {
                    Metric.AREA -> 6 * cube.getEdge() * cube.getEdge()
                    Metric.PERIMETER -> 12 * cube.getEdge()
                    Metric.VOLUME -> Math.pow(cube.getEdge(), 3.0)
                }
            }
        }
    }
}

class ShapeExporter {
    fun toJson(shape: Shape): String {
        return when (shape.getKind()) {
            ShapeKind.RECTANGLE -> {
                val r = shape as Rectangle
                String.format(
                    "{\"type\":\"rectangle\",\"color\":\"%s\",\"width\":%.2f,\"height\":%.2f}",
                    r.getColor(), r.getWidth(), r.getHeight()
                )
            }
            ShapeKind.CIRCLE -> {
                val c = shape as Circle
                String.format(
                    "{\"type\":\"circle\",\"color\":\"%s\",\"radius\":%.2f}",
                    c.getColor(), c.getRadius()
                )
            }
            ShapeKind.CUBE -> {
                val cube = shape as Cube
                String.format(
                    "{\"type\":\"cube\",\"color\":\"%s\",\"edge\":%.2f}",
                    cube.getColor(), cube.getEdge()
                )
            }
        }
    }
}

class ShapeFactory {
    fun create(spec: String): Shape {
        val parts = spec.split(":", limit = 2)
        val type = parts[0].trim()
        val kvs = if (parts.size > 1) parts[1] else ""
        val map = mutableMapOf<String, String>()
        for (kv in kvs.split(";")) {
            val p = kv.split("=")
            if (p.size == 2) map[p[0].trim()] = p[1].trim()
        }
        val color = map.getOrDefault("color", "black")
        return when (type.lowercase()) {
            "rectangle" -> Rectangle(
                map.getOrDefault("width", "0").toDouble(),
                map.getOrDefault("height", "0").toDouble(),
                color
            )
            "circle" -> Circle(
                map.getOrDefault("radius", "0").toDouble(),
                color
            )
            "cube" -> Cube(
                map.getOrDefault("edge", "0").toDouble(),
                color
            )
            else -> throw IllegalArgumentException("Unknown shape: $type")
        }
    }
}

