class Circle(private val radius: Double, color: String) : TwoDShape(color) {
    fun getRadius(): Double = radius

    override fun area(): Double = Math.PI * radius * radius
    override fun perimeter(): Double = 2 * Math.PI * radius

    override fun toJson(): String =
        String.format("{\"type\":\"circle\",\"color\":\"%s\",\"radius\":%.2f}",
            getColor(), radius)
}

class Cube(private val edge: Double, color: String) : ThreeDShape(color) {
    fun getEdge(): Double = edge

    override fun area(): Double = 6 * edge * edge
    override fun perimeter(): Double = 12 * edge
    override fun volume(): Double = Math.pow(edge, 3.0)

    override fun toJson(): String =
        String.format("{\"type\":\"cube\",\"color\":\"%s\",\"edge\":%.2f}",
            getColor(), edge)
}

enum class Metric { AREA, PERIMETER, VOLUME }

class MetricCalculator {
    fun compute(shape: Shape, metric: Metric): Double =
        when (metric) {
            Metric.AREA -> shape.area()
            Metric.PERIMETER -> shape.perimeter()
            Metric.VOLUME -> shape.volume()
        }
}

class ShapeExporter {
    fun toJson(shape: Shape): String = shape.toJson()
}

class ShapeFactory {
    fun create(spec: String): Shape {
        val parts = spec.split(":", limit = 2)
        val type = parts[0].trim()
        val kvs = if (parts.size > 1) parts[1].split(";") else emptyList()
        val map = mutableMapOf<String, String>()
        for (kv in kvs) {
            val p = kv.split("=")
            if (p.size == 2) map[p[0].trim()] = p[1].trim()
        }
        val color = map.getOrDefault("color", "black")

        return when (type.lowercase()) {
            "rectangle" -> Rectangle(
                map.getOrDefault("width", "0").toDouble(),
                map.getOrDefault("height", "0").toDouble(),
                color
            )
            "circle" -> Circle(
                map.getOrDefault("radius", "0").toDouble(),
                color
            )
            "cube" -> Cube(
                map.getOrDefault("edge", "0").toDouble(),
                color
            )
            else -> throw IllegalArgumentException("Unknown shape: $type")
        }
    }
}

