enum class FoodType { BURGER, FRIES, PIZZA, DRINK, SALAD, DESSERT }
enum class Size { SMALL, MEDIUM, LARGE }

class FoodItem(private val name: String, private val type: FoodType, private val size: Size, extras: MutableSet<String>?) {
