interface FoodOps {
    fun type(): FoodType
    fun tax(): Double
    fun discount(item: FoodItem): Double
    fun station(): String
    fun prepare()
    fun serve()
    fun reserve(inventory: InventoryManager, item: FoodItem): Boolean
}

object FoodOpsRegistry {
    private val OPS: MutableMap<FoodType, FoodOps> = EnumMap(FoodType::class.java)
    fun register(ops: FoodOps) { OPS[ops.type()] = ops }
    fun of(t: FoodType): FoodOps {
        val o = OPS[t]
        if (o == null) throw IllegalArgumentException("Unknown type")
        return o
    }
    init {
        register(BurgerOps())
        register(FriesOps())
        register(PizzaOps())
        register(DrinkOps())
        register(SaladOps())
        register(DessertOps())
    }
}

class BurgerOps : FoodOps {
    override fun type(): FoodType = FoodType.BURGER
