import java.util.*

enum class DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER }
enum class RoomType { LIVING, KITCHEN, BEDROOM, GARDEN }
enum class Mode { ECO, COMFORT, AWAY, NIGHT }

class SmartDevice(private val id: String, private val type: DeviceType) {
    private var mode: Mode = Mode.ECO
    private var online: Boolean = true
    fun getId(): String = id
    fun getType(): DeviceType = type
    fun getMode(): Mode = mode
    fun isOnline(): Boolean = online
    fun setMode(m: Mode) { this.mode = m }
    fun setOnline(o: Boolean) { this.online = o }
}

class Room(private val name: String, private val type: RoomType) {
    private val devices: MutableList<SmartDevice> = ArrayList()
    fun add(d: SmartDevice) { devices.add(d) }
    fun getDevices(): List<SmartDevice> = devices
    fun getName(): String = name
    fun getType(): RoomType = type
}

class ScheduleEntry(val device: SmartDevice, val cron: String, val action: String)

