        base = 0
        if c.type == CharacterType.WARRIOR:
            base = int(c.stats.atk * (1.1 + c.level * 0.03)) - e.stats.def_ // 3
            if s.type == SkillType.SPECIAL:
                base += 12
        elif c.type == CharacterType.MAGE:
            base = c.stats.mag * 2 + (18 if s.type == SkillType.SPECIAL else 0) - e.stats.res // 2
        elif c.type == CharacterType.ARCHER:
            base = c.stats.atk + c.stats.spd // 2 + (10 if s.type == SkillType.SPECIAL else 0) - e.stats.def_ // 4
        elif c.type == CharacterType.ROGUE:
            base = c.stats.atk + c.stats.spd + (14 if s.type == SkillType.SPECIAL else 0) - e.stats.def_ // 5
        base = self.wr.apply_weapon(c, base)
        base += self.pets.offense_boost(c)
        if self.pets.pet_assist(c):
            base += self.pets.pet_assist_damage(c)
        critChance = self.wr.crit_chance(c) + (10 if c.type == CharacterType.ARCHER else 0)
        if self.rng.randint(0, 99) < critChance:
            base *= 2
        return max(0, base)


class HealCalculator:
