        amt = 0
        if c.type == CharacterType.WARRIOR:
            amt = c.stats.atk // 3 + 6
        elif c.type == CharacterType.MAGE:
            amt = c.stats.mag + 14
        elif c.type == CharacterType.ARCHER:
            amt = c.stats.atk // 4 + c.stats.spd // 5
        elif c.type == CharacterType.ROGUE:
            amt = c.stats.atk // 2
        if s.type == SkillType.SPECIAL:
            amt += 6
        amt += self.pets.defense_boost(c) // 2 + self.pets.pet_assist_heal(c)
        return max(0, amt)


class LootCalculator:
