            return 10 + c.level + e.stats.def_ // 5
        if c.type == CharacterType.MAGE:
            return 9 + c.level * 2 + e.stats.res // 4
        if c.type == CharacterType.ARCHER:
            return 11 + c.level + e.stats.def_ // 6
        if c.type == CharacterType.ROGUE:
            return 14 + c.level
        return 0


class TargetingSystem:
    def pick(self, enemies):
        t = enemies[0]
        for e in enemies:
            if e.stats.hp < t.stats.hp:
                t = e
        return t


class SkillSelector:
    def choose(self, c):
        for s in c.skills:
            if s.type != SkillType.BASIC:
                return s
        return c.skills[0]


class TurnEngine:
    def __init__(self):
        self.dmg = DamageCalculator()
        self.healCalc = HealCalculator()
        self.loot = LootCalculator()
        self.targeting = TargetingSystem()
        self.selector = SkillSelector()

    def turn(self, party, enemies):
        for c in party:
            s = self.selector.choose(c)
            if s.type == SkillType.SUPPORT:
                h = self.healCalc.heal(c, s)
                c.stats.hp += h
                continue
            target = self.targeting.pick(enemies)
            d = self.dmg.calc(c, s, target)
            target.stats.hp = max(0, target.stats.hp - d)

    def total_gold(self, party, e):
        g = 0
        for c in party:
            g += self.loot.gold(c, e)
        return g

