from enum import Enum
import random


class FoodType(Enum):
    BURGER = 1
    FRIES = 2
    PIZZA = 3
    DRINK = 4
    SALAD = 5
    DESSERT = 6


class Size(Enum):
    SMALL = 1
    MEDIUM = 2
    LARGE = 3


class FoodItem:
    def __init__(self, name, type_, size, extras=None):
        self._name = name
        self._type = type_
        self._size = size
        self._extras = set() if extras is None else extras

    def getName(self): return self._name
    def getType(self): return self._type
    def getSize(self): return self._size
    def getExtras(self): return self._extras


class Order:
    def __init__(self, customer):
        self._customer = customer
        self._items = []

    def add(self, item): self._items.append(item)
    def items(self): return self._items
    def customer(self): return self._customer


class PriceCalculator:
    def price(self, item):
        if item.getType() == FoodType.BURGER:
            return {Size.SMALL: 5.49, Size.MEDIUM: 6.49}.get(item.getSize(), 7.49)
        if item.getType() == FoodType.FRIES:
            return {Size.SMALL: 1.99, Size.MEDIUM: 2.49}.get(item.getSize(), 2.99)
        if item.getType() == FoodType.PIZZA:
            return {Size.SMALL: 7.99, Size.MEDIUM: 9.99}.get(item.getSize(), 12.49)
        if item.getType() == FoodType.DRINK:
            return {Size.SMALL: 1.29, Size.MEDIUM: 1.49}.get(item.getSize(), 1.79)
        if item.getType() == FoodType.SALAD:
            return {Size.SMALL: 4.49, Size.MEDIUM: 5.49}.get(item.getSize(), 6.49)
        if item.getType() == FoodType.DESSERT:
            return {Size.SMALL: 2.99, Size.MEDIUM: 3.49}.get(item.getSize(), 3.99)
        raise ValueError("Unknown type")


class TaxCalculator:
    def taxFor(self, item):
        if item.getType() == FoodType.DRINK: return 0.10
        if item.getType() == FoodType.DESSERT: return 0.08
        if item.getType() == FoodType.PIZZA: return 0.06
        return 0.05


class NutritionCalculator:
    def calories(self, item):
        t, s = item.getType(), item.getSize()
        if t == FoodType.BURGER: return {Size.SMALL: 480, Size.MEDIUM: 650}.get(s, 820)
        if t == FoodType.FRIES: return {Size.SMALL: 220, Size.MEDIUM: 330}.get(s, 440)
        if t == FoodType.PIZZA: return {Size.SMALL: 700, Size.MEDIUM: 950}.get(s, 1200)
        if t == FoodType.DRINK: return {Size.SMALL: 110, Size.MEDIUM: 160}.get(s, 210)
        if t == FoodType.SALAD: return {Size.SMALL: 180, Size.MEDIUM: 260}.get(s, 340)
        if t == FoodType.DESSERT: return {Size.SMALL: 250, Size.MEDIUM: 320}.get(s, 390)
        return 0


class PromotionEngine:
    def discount(self, item):
        t = item.getType()
        if t == FoodType.BURGER: return 0.50 if "extra-cheese" in item.getExtras() else 0.0
        if t == FoodType.FRIES: return 0.20 if item.getSize() == Size.LARGE else 0.0
        if t == FoodType.DRINK: return 0.10 if "no-ice" in item.getExtras() else 0.0
        if t == FoodType.SALAD: return 0.30 if "chicken" in item.getExtras() else 0.0
        if t == FoodType.PIZZA: return 0.40 if "pepperoni" in item.getExtras() else 0.0
        return 0.0


class KitchenStationRouter:
    def station(self, item):
        return {
            FoodType.BURGER: "Grill",
            FoodType.FRIES: "Fryer",
            FoodType.PIZZA: "Oven",
            FoodType.DRINK: "Beverage",
            FoodType.SALAD: "Cold Prep",
            FoodType.DESSERT: "Dessert Bar"
        }.get(item.getType(), "Unknown")


class InventoryManager:
    def __init__(self):
        self._stock = {"bun": 50, "patty": 50, "potato": 80,
                       "dough": 30, "soda": 100, "greens": 40, "dessertBase": 30}

    def reserve(self, item):
        t = item.getType()
        if t == FoodType.BURGER: return self._take("bun", 1) and self._take("patty", 1)
        if t == FoodType.FRIES: return self._take("potato", 2)
        if t == FoodType.PIZZA: return self._take("dough", 1)
        if t == FoodType.DRINK: return self._take("soda", 1)
        if t == FoodType.SALAD: return self._take("greens", 1)
        if t == FoodType.DESSERT: return self._take("dessertBase", 1)
        return False

    def _take(self, key, qty):
        have = self._stock.get(key, 0)
        if have < qty: return False
        self._stock[key] = have - qty
        return True

    def getStock(self): return dict(self._stock)


class Chef:
