        elif t == DeviceType.THERMOSTAT: print("Thermostat", id_, "heating")
        elif t == DeviceType.DOOR_LOCK: print("Lock", id_, "locked")
        elif t == DeviceType.CAMERA: print("Camera", id_, "recording")
        elif t == DeviceType.SPRINKLER: print("Sprinkler", id_, "watering")
        elif t == DeviceType.BLIND: print("Blind", id_, "open")
        elif t == DeviceType.SPEAKER: print("Speaker", id_, "playing")

    def turnOff(self, d):
        t, id_ = d.getType(), d.getId()
        if t == DeviceType.LIGHT: print("Light", id_, "OFF")
        elif t == DeviceType.THERMOSTAT: print("Thermostat", id_, "idle")
        elif t == DeviceType.DOOR_LOCK: print("Lock", id_, "secured")
        elif t == DeviceType.CAMERA: print("Camera", id_, "standby")
        elif t == DeviceType.SPRINKLER: print("Sprinkler", id_, "stopped")
        elif t == DeviceType.BLIND: print("Blind", id_, "closed")
        elif t == DeviceType.SPEAKER: print("Speaker", id_, "paused")

    def setMode(self, d, m):
        d.setMode(m)
        print(d.getType().name.capitalize(), d.getId(), "->", m)

    def energyUsageKwh(self, d, minutes):
        t = d.getType()
        if t == DeviceType.LIGHT: return 0.012 * minutes
        if t == DeviceType.THERMOSTAT: return 0.08 * minutes
        if t == DeviceType.DOOR_LOCK: return 0.0002 * minutes
        if t == DeviceType.CAMERA: return 0.01 * minutes
        if t == DeviceType.SPRINKLER: return 0.03 * minutes
        if t == DeviceType.BLIND: return 0 if minutes == 0 else 0.001 * minutes
        if t == DeviceType.SPEAKER: return 0.015 * minutes
        return 0


class NotificationService:
    def push(self, d, t, msg):
        print(f"[{t.name}] {d.getType().name.title()} {d.getId()}: {msg}")


class DiagnosticsService:
    def health(self, d):
        t = d.getType()
        if t == DeviceType.LIGHT: return "OK" if d.isOnline() else "Bulb Offline"
        if t == DeviceType.THERMOSTAT: return "OK" if d.isOnline() else "Sensor Offline"
        if t == DeviceType.DOOR_LOCK: return "OK" if d.isOnline() else "Battery Low"
        if t == DeviceType.CAMERA: return "OK" if d.isOnline() else "Stream Error"
        if t == DeviceType.SPRINKLER: return "OK" if d.isOnline() else "Valve Error"
        if t == DeviceType.BLIND: return "OK" if d.isOnline() else "Motor Jam"
        if t == DeviceType.SPEAKER: return "OK" if d.isOnline() else "Audio Fault"
        return "Unknown"


class SecurityPolicy:
    def allowed(self, d, action, userHome):
        t = d.getType()
        if t == DeviceType.DOOR_LOCK: return action == "unlock" and userHome or action != "unlock"
        if t == DeviceType.CAMERA: return action != "disable" or userHome
        if t == DeviceType.SPRINKLER: return not (action == "on" and userHome and d.getMode() == Mode.PARTY)
        if t == DeviceType.SPEAKER: return not (action == "play" and not userHome)
        return True


class VoiceCommandProcessor:
    def __init__(self): self.ctl = SmartHomeController()
    def handle(self, phrase, d):
        t = d.getType()
        if t == DeviceType.LIGHT:
            if phrase.lower() == "lights on": self.ctl.turnOn(d)
            elif phrase.lower() == "lights off": self.ctl.turnOff(d)
        elif t == DeviceType.THERMOSTAT:
            if phrase.lower() == "heat on": self.ctl.turnOn(d)
            elif phrase.lower() == "eco": self.ctl.setMode(d, Mode.ECO)
        elif t == DeviceType.DOOR_LOCK and phrase.lower() == "lock door": self.ctl.turnOn(d)
        elif t == DeviceType.CAMERA and phrase.lower() == "record": self.ctl.turnOn(d)
        elif t == DeviceType.SPRINKLER and phrase.lower() == "water": self.ctl.turnOn(d)
        elif t == DeviceType.BLIND:
            if phrase.lower() == "open blinds": self.ctl.turnOn(d)
            elif phrase.lower() == "close blinds": self.ctl.turnOff(d)
        elif t == DeviceType.SPEAKER:
            if phrase.lower() == "play music": self.ctl.turnOn(d)
            elif phrase.lower() == "pause music": self.ctl.turnOff(d)


class SceneManager:
    def __init__(self): self.ctl = SmartHomeController()
    def apply(self, scene, devices):
        for d in devices:
            if scene == "Evening":
                if d.getType() == DeviceType.LIGHT: self.ctl.turnOn(d); self.ctl.setMode(d, Mode.COMFORT)
                elif d.getType() == DeviceType.THERMOSTAT: self.ctl.setMode(d, Mode.COMFORT)
                elif d.getType() == DeviceType.CAMERA: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.DOOR_LOCK: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.SPRINKLER: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.BLIND: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.SPEAKER: self.ctl.turnOn(d)
            elif scene == "Away":
                if d.getType() == DeviceType.LIGHT: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.THERMOSTAT: self.ctl.setMode(d, Mode.AWAY)
                elif d.getType() == DeviceType.CAMERA: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.DOOR_LOCK: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.SPRINKLER: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.BLIND: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.SPEAKER: self.ctl.turnOff(d)
            elif scene == "Party":
                if d.getType() == DeviceType.LIGHT: self.ctl.turnOn(d); self.ctl.setMode(d, Mode.PARTY)
                elif d.getType() == DeviceType.THERMOSTAT: self.ctl.setMode(d, Mode.COMFORT)
                elif d.getType() == DeviceType.SPEAKER: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.CAMERA: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.BLIND: self.ctl.turnOn(d)
                elif d.getType() == DeviceType.SPRINKLER: self.ctl.turnOff(d)
                elif d.getType() == DeviceType.DOOR_LOCK: self.ctl.turnOn(d)


class FirmwareManager:
    def steps(self, d):
        t = d.getType()
        if t == DeviceType.LIGHT: return ["Ping","PushFW","Reboot"]
        if t == DeviceType.THERMOSTAT: return ["Ping","Backup","PushFW","Recalibrate"]
        if t == DeviceType.DOOR_LOCK: return ["SecureChannel","PushFW","Reauth"]
        if t == DeviceType.CAMERA: return ["Ping","PushFW","RestartStream"]
        if t == DeviceType.SPRINKLER: return ["Ping","PushFW","ValveTest"]
        if t == DeviceType.BLIND: return ["Ping","PushFW","AlignMotor"]
        if t == DeviceType.SPEAKER: return ["Ping","PushFW","RestartAudio"]
        return []


class ScheduleEngine:
    class Entry:
        def __init__(self, d, cron, action):
            self.device, self.cron, self.action = d, cron, action
    def run(self, e, ctl):
        t, a = e.device.getType(), e.action
        if t == DeviceType.LIGHT: ctl.turnOn(e.device) if a=="on" else ctl.turnOff(e.device)
        elif t == DeviceType.SPRINKLER: ctl.turnOn(e.device) if a=="on" else ctl.turnOff(e.device)
        elif t == DeviceType.THERMOSTAT and a=="comfort": ctl.setMode(e.device, Mode.COMFORT)
        elif t == DeviceType.BLIND: ctl.turnOn(e.device) if a=="open" else ctl.turnOff(e.device)
        elif t == DeviceType.SPEAKER: ctl.turnOn(e.device) if a=="play" else ctl.turnOff(e.device)
        else: ctl.turnOff(e.device)


class WeatherAdvisor:
    def shouldWater(self, condition): return condition=="dry"
    def lightLevelFor(self, condition):
        return {"sunny":100,"cloudy":70,"rain":50}.get(condition,60)


class GeoFence:
    def inside(self, user, distanceMeters):
        if user=="owner": return distanceMeters<50
        if user=="guest": return distanceMeters<10
        return False


class AccessController:
