        t = d.getType()
        if t == DeviceType.DOOR_LOCK: return role=="owner"
        if t == DeviceType.CAMERA: return role in ("owner","admin")
        if t == DeviceType.THERMOSTAT: return role!="guest"
        return True


class DeviceRenderer:
