        return {
            DeviceType.LIGHT:"💡", DeviceType.THERMOSTAT:"🌡️", DeviceType.DOOR_LOCK:"🔒",
            DeviceType.CAMERA:"📷", DeviceType.SPRINKLER:"💧", DeviceType.BLIND:"🪟",
            DeviceType.SPEAKER:"🔊"
        }.get(d.getType(),"❓")
