from enum import Enum
from abc import ABC, abstractmethod

class DeviceType(Enum):
    LIGHT = 1
    THERMOSTAT = 2
    DOOR_LOCK = 3
    CAMERA = 4
    SPRINKLER = 5
    BLIND = 6
    SPEAKER = 7

class RoomType(Enum):
    LIVING = 1
    KITCHEN = 2
    BEDROOM = 3
    BATHROOM = 4
    GARDEN = 5
    GARAGE = 6

class Mode(Enum):
    ECO = 1
    COMFORT = 2
    AWAY = 3
    NIGHT = 4
    PARTY = 5

class AlertType(Enum):
    INFO = 1
    WARN = 2
    CRITICAL = 3

class SmartDevice(ABC):
    def __init__(self, id, type):
        self.id = id
        self.type = type
        self.mode = Mode.ECO
        self.online = True

    def getId(self): return self.id
    def getType(self): return self.type
    def getMode(self): return self.mode
    def isOnline(self): return self.online
    def setMode(self, m): self.mode = m
    def setOnline(self, o): self.online = o

    @abstractmethod
    def turnOn(self): ...
    @abstractmethod
    def turnOff(self): ...
    @abstractmethod
    def setModeAction(self, m): ...
    @abstractmethod
    def getEnergyUsagePerMinute(self): ...
    @abstractmethod
    def getHealthStatus(self): ...
    @abstractmethod
    def getNotificationPrefix(self): ...
    @abstractmethod
    def isActionAllowed(self, action, userHome): ...
    @abstractmethod
    def handleVoiceCommand(self, phrase, ctl): ...
    @abstractmethod
    def applyEveningScene(self, ctl): ...
    @abstractmethod
    def applyAwayScene(self, ctl): ...
    @abstractmethod
    def applyPartyScene(self, ctl): ...
    @abstractmethod
    def getFirmwareSteps(self): ...
    @abstractmethod
    def executeScheduledAction(self, action, ctl): ...
    @abstractmethod
    def handleMotionEvent(self, ctl): ...
    @abstractmethod
    def handleSunsetEvent(self, ctl): ...
    @abstractmethod
    def handleDoorOpenEvent(self, ctl): ...
    @abstractmethod
    def getIcon(self): ...
    @abstractmethod
    def getLabel(self): ...
    @abstractmethod
    def getGroups(self): ...
    @abstractmethod
    def canUserControl(self, role): ...

