class ShapeKind(Enum):
    RECTANGLE = 1
    CIRCLE = 2
    CUBE = 3


class Shape(GraphicObject):
    def __init__(self, kind: ShapeKind, color: str):
        super().__init__(color)
        self._kind = kind

    def get_kind(self):
        return self._kind


class TwoDShape(Shape):
    def __init__(self, kind: ShapeKind, color: str):
        super().__init__(kind, color)


class ThreeDShape(Shape):
    def __init__(self, kind: ShapeKind, color: str):
        super().__init__(kind, color)


class Rectangle(TwoDShape):
    def __init__(self, width: float, height: float, color: str):
        super().__init__(ShapeKind.RECTANGLE, color)
        self._width = width
        self._height = height

    def get_width(self):
        return self._width

    def get_height(self):
        return self._height


class Circle(TwoDShape):
    def __init__(self, radius: float, color: str):
        super().__init__(ShapeKind.CIRCLE, color)
        self._radius = radius

    def get_radius(self):
        return self._radius


class Cube(ThreeDShape):
    def __init__(self, edge: float, color: str):
        super().__init__(ShapeKind.CUBE, color)
        self._edge = edge

    def get_edge(self):
        return self._edge


class Metric(Enum):
    AREA = 1
    PERIMETER = 2
    VOLUME = 3


class MetricCalculator:
    def compute(self, shape: Shape, metric: Metric):
        if shape.get_kind() == ShapeKind.RECTANGLE:
            r: Rectangle = shape
            if metric == Metric.AREA:
                return r.get_width() * r.get_height()
            elif metric == Metric.PERIMETER:
                return 2 * (r.get_width() + r.get_height())
            elif metric == Metric.VOLUME:
                return 0
        elif shape.get_kind() == ShapeKind.CIRCLE:
            c: Circle = shape
            if metric == Metric.AREA:
                return math.pi * c.get_radius() ** 2
            elif metric == Metric.PERIMETER:
                return 2 * math.pi * c.get_radius()
            elif metric == Metric.VOLUME:
                return 0
        elif shape.get_kind() == ShapeKind.CUBE:
            cube: Cube = shape
            if metric == Metric.AREA:
                return 6 * cube.get_edge() ** 2
            elif metric == Metric.PERIMETER:
                return 12 * cube.get_edge()
            elif metric == Metric.VOLUME:
                return cube.get_edge() ** 3
        return 0


class ShapeExporter:
    def to_json(self, shape: Shape):
        if shape.get_kind() == ShapeKind.RECTANGLE:
            r: Rectangle = shape
            return '{{"type":"rectangle","color":"{}","width":{:.2f},"height":{:.2f}}}'.format(
                r.get_color(), r.get_width(), r.get_height()
            )
        elif shape.get_kind() == ShapeKind.CIRCLE:
            c: Circle = shape
            return '{{"type":"circle","color":"{}","radius":{:.2f}}}'.format(
                c.get_color(), c.get_radius()
            )
        elif shape.get_kind() == ShapeKind.CUBE:
            cube: Cube = shape
            return '{{"type":"cube","color":"{}","edge":{:.2f}}}'.format(
                cube.get_color(), cube.get_edge()
            )
        return "{}"


class ShapeFactory:
    def create(self, spec: str) -> Shape:
        parts = spec.split(":", 1)
        type_ = parts[0].strip()
        kvs = (parts[1] if len(parts) > 1 else "").split(";")
        map_ = {}
        for kv in kvs:
            p = kv.split("=")
            if len(p) == 2:
                map_[p[0].strip()] = p[1].strip()
        color = map_.get("color", "black")

        if type_.lower() == "rectangle":
            return Rectangle(
                float(map_.get("width", "0")),
                float(map_.get("height", "0")),
                color
            )
        elif type_.lower() == "circle":
            return Circle(
                float(map_.get("radius", "0")),
                color
            )
        elif type_.lower() == "cube":
            return Cube(
                float(map_.get("edge", "0")),
                color
            )
        else:
            raise ValueError("Unknown shape: " + type_)

