        self._color = color

    def get_color(self) -> str:
        return self._color


class Shape(GraphicObject):
    @abstractmethod
    def area(self) -> float:
        pass

    @abstractmethod
    def perimeter(self) -> float:
        pass

    @abstractmethod
    def volume(self) -> float:
        pass

    @abstractmethod
    def to_json(self) -> str:
        pass


class TwoDShape(Shape):
    def volume(self) -> float:
        return 0.0


