        super().__init__(color)
        self._width = width
        self._height = height

    def get_width(self) -> float:
        return self._width

    def get_height(self) -> float:
        return self._height

    def area(self) -> float:
        return self._width * self._height

    def perimeter(self) -> float:
        return 2 * (self._width + self._height)

    def to_json(self) -> str:
        return '{{"type":"rectangle","color":"{}","width":{:.2f},"height":{:.2f}}}'.format(
            self.get_color(), self._width, self._height
        )


