    SALAD = 5
    DESSERT = 6


class Size(Enum):
    SMALL = 1
    MEDIUM = 2
    LARGE = 3


class FoodItem:
    def __init__(self, name: str, type_: FoodType, size: Size, extras: Set[str] = None):
        self._name = name
        self._type = type_
        self._size = size
        self._extras = extras if extras else set()

    def get_name(self): return self._name
    def get_type(self): return self._type
    def get_size(self): return self._size
    def get_extras(self): return self._extras


class Order:
