        self._customer = customer
        self._items: List[FoodItem] = []

    def add(self, item: FoodItem):
        self._items.append(item)

    def items(self): return self._items
    def customer(self): return self._customer


class PriceCalculator:
    def price(self, item: FoodItem) -> float:
        if item.get_type() == FoodType.BURGER:
            return {Size.SMALL: 5.49, Size.MEDIUM: 6.49, Size.LARGE: 7.49}[item.get_size()]
        elif item.get_type() == FoodType.FRIES:
