        elif item.get_type() == FoodType.PIZZA:
            return {Size.SMALL: 7.99, Size.MEDIUM: 9.99, Size.LARGE: 12.49}[item.get_size()]
        elif item.get_type() == FoodType.DRINK:
            return {Size.SMALL: 1.29, Size.MEDIUM: 1.49, Size.LARGE: 1.79}[item.get_size()]
        elif item.get_type() == FoodType.SALAD:
            return {Size.SMALL: 4.49, Size.MEDIUM: 5.49, Size.LARGE: 6.49}[item.get_size()]
        elif item.get_type() == FoodType.DESSERT:
            return {Size.SMALL: 2.99, Size.MEDIUM: 3.49, Size.LARGE: 3.99}[item.get_size()]
