            return {Size.SMALL: 480, Size.MEDIUM: 650, Size.LARGE: 820}[item.get_size()]
        elif item.get_type() == FoodType.FRIES:
            return {Size.SMALL: 220, Size.MEDIUM: 330, Size.LARGE: 440}[item.get_size()]
        elif item.get_type() == FoodType.PIZZA:
            return {Size.SMALL: 700, Size.MEDIUM: 950, Size.LARGE: 1200}[item.get_size()]
        elif item.get_type() == FoodType.DRINK:
            return {Size.SMALL: 110, Size.MEDIUM: 160, Size.LARGE: 210}[item.get_size()]
        elif item.get_type() == FoodType.SALAD:
            return {Size.SMALL: 180, Size.MEDIUM: 260, Size.LARGE: 340}[item.get_size()]
        elif item.get_type() == FoodType.DESSERT:
            return {Size.SMALL: 250, Size.MEDIUM: 320, Size.LARGE: 390}[item.get_size()]
        else:
            return 0


class PromotionEngine:
    def discount(self, item: FoodItem) -> float:
        if item.get_type() == FoodType.BURGER:
            return 0.50 if "extra-cheese" in item.get_extras() else 0.0
        elif item.get_type() == FoodType.FRIES:
            return 0.20 if item.get_size() == Size.LARGE else 0.0
        elif item.get_type() == FoodType.DRINK:
            return 0.10 if "no-ice" in item.get_extras() else 0.0
        elif item.get_type() == FoodType.SALAD:
            return 0.30 if "chicken" in item.get_extras() else 0.0
        elif item.get_type() == FoodType.PIZZA:
            return 0.40 if "pepperoni" in item.get_extras() else 0.0
        else:
            return 0.0


class KitchenStationRouter:
    def station(self, item: FoodItem) -> str:
        return {
            FoodType.BURGER: "Grill",
            FoodType.FRIES: "Fryer",
            FoodType.PIZZA: "Oven",
            FoodType.DRINK: "Beverage",
            FoodType.SALAD: "Cold Prep",
            FoodType.DESSERT: "Dessert Bar",
        }[item.get_type()]


class InventoryManager:
    def __init__(self):
        self._stock: Dict[str, int] = {
            "bun": 50, "patty": 50, "potato": 80,
            "dough": 30, "soda": 100, "greens": 40, "dessertBase": 30
        }

    def reserve(self, item: FoodItem) -> bool:
        if item.get_type() == FoodType.BURGER:
            return self._take("bun", 1) and self._take("patty", 1)
        elif item.get_type() == FoodType.FRIES:
            return self._take("potato", 2)
        elif item.get_type() == FoodType.PIZZA:
            return self._take("dough", 1)
        elif item.get_type() == FoodType.DRINK:
            return self._take("soda", 1)
        elif item.get_type() == FoodType.SALAD:
            return self._take("greens", 1)
        elif item.get_type() == FoodType.DESSERT:
            return self._take("dessertBase", 1)
        return False

    def _take(self, key: str, qty: int) -> bool:
        have = self._stock.get(key, 0)
        if have < qty: return False
        self._stock[key] = have - qty
        return True

    def get_stock(self) -> Dict[str, int]:
        return dict(self._stock)


class Chef:
    def prepare(self, item: FoodItem):
        actions = {
            FoodType.BURGER: "Chef grills patty and assembles burger",
            FoodType.FRIES: "Chef drops fries in fryer",
            FoodType.PIZZA: "Chef stretches dough and bakes pizza",
            FoodType.DRINK: "Chef fills cup and lids drink",
            FoodType.SALAD: "Chef chops and tosses salad",
            FoodType.DESSERT: "Chef plates dessert",
        }
        print(actions[item.get_type()])


class Waiter:
    def take(self, order: Order, item: FoodItem):
        print("Waiter records item:", item.get_name())
        order.add(item)

    def serve(self, item: FoodItem):
        actions = {
            FoodType.BURGER: "Waiter serves burger with napkins",
            FoodType.FRIES: "Waiter serves fries with ketchup",
            FoodType.PIZZA: "Waiter serves pizza with slicer",
            FoodType.DRINK: "Waiter serves drink with straw",
            FoodType.SALAD: "Waiter serves salad with dressing",
            FoodType.DESSERT: "Waiter serves dessert with spoon",
        }
        print(actions[item.get_type()])


class Cashier:
    def __init__(self):
        self._prices = PriceCalculator()
        self._taxes = TaxCalculator()
        self._promos = PromotionEngine()

    def total(self, order: Order) -> float:
        sum_ = 0.0
        for item in order.items():
            base = self._prices.price(item)
            discount = self._promos.discount(item)
            taxed = (base - discount) * (1.0 + self._taxes.tax_for(item))
            sum_ += taxed
        return round(sum_, 2)


class ReceiptPrinter:
    def __init__(self):
        self._prices = PriceCalculator()
        self._taxes = TaxCalculator()
        self._promos = PromotionEngine()
        self._nutrition = NutritionCalculator()

    def print(self, order: Order):
        print("Customer:", order.customer())
        for item in order.items():
            base = self._prices.price(item)
            discount = self._promos.discount(item)
            tax_rate = self._taxes.tax_for(item)
            kcal = self._nutrition.calories(item)
            print(f"{item.get_name()} ({item.get_type().name} {item.get_size().name}) "
                  f"base ${base} disc ${discount} tax {int(tax_rate*100)}% kcal {kcal}")

