class InventoryManager:
    def __init__(self):
        self._stock: Dict[str, int] = {
            "bun": 50,
            "patty": 50,
            "potato": 80,
            "dough": 30,
            "soda": 100,
            "greens": 40,
            "dessertBase": 30,
        }

    def reserve(self, item: FoodItem) -> bool:
        return FoodOpsRegistry.of(item.getType()).reserve(self, item)

    def consume(self, key: str, qty: int) -> bool:
        return self._take(key, qty)

    def _take(self, key: str, qty: int) -> bool:
        have = self._stock.get(key, 0)
        if have < qty:
            return False
        self._stock[key] = have - qty
        return True

    def getStock(self) -> Dict[str, int]:
        return dict(self._stock)


class Chef:
    def prepare(self, item: FoodItem) -> None:
        FoodOpsRegistry.of(item.getType()).prepare()


class Waiter:
    def take(self, order: Order, item: FoodItem) -> None:
        print("Waiter records item: " + item.getName())
        order.add(item)

    def serve(self, item: FoodItem) -> None:
        FoodOpsRegistry.of(item.getType()).serve()


class Cashier:
    def __init__(self):
        self._prices = PriceCalculator()
        self._taxes = TaxCalculator()
        self._promos = PromotionEngine()

    def total(self, order: Order) -> float:
        sum_ = 0.0
        for item in order.items():
            base = self._prices.price(item)
            discount = self._promos.discount(item)
            taxed = (base - discount) * (1.0 + self._taxes.taxFor(item))
            sum_ += taxed
        return int(sum_ * 100.0 + 0.5) / 100.0


class ReceiptPrinter:
    def __init__(self):
        self._prices = PriceCalculator()
        self._taxes = TaxCalculator()
        self._promos = PromotionEngine()
        self._nutrition = NutritionCalculator()

    def print(self, order: Order) -> None:
        print("Customer: " + order.customer())
        for item in order.items():
            base = self._prices.price(item)
            discount = self._promos.discount(item)
            taxRate = self._taxes.taxFor(item)
            kcal = self._nutrition.calories(item)
            print(
                f"{item.getName()} ({item.getType().name} {item.getSize().name}) "
                f"base ${base} disc ${discount} tax {int(taxRate*100)}% kcal {kcal}"
            )

